import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

export default function Barbers() {
  const [barbers, setBarbers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost/barbershop/frontend/backend/api/get_barbers.php")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setBarbers(data);
      })
      .catch(() => setBarbers([]));
  }, []);

  const handleSelectBarber = (barber) => {
    localStorage.setItem("selectedBarber", JSON.stringify(barber));
    navigate("/booking");
  };

  return (
    <section className="min-h-screen bg-gradient-to-b from-[#0a0a0a] to-[#1a1a1a] text-white py-20 overflow-hidden relative">
      {/* ‚ú® background glowing lights */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute w-[400px] h-[400px] bg-[#d4af37]/20 blur-[150px] top-20 left-[-150px] rounded-full"></div>
        <div className="absolute w-[500px] h-[500px] bg-[#d4af37]/10 blur-[180px] bottom-10 right-[-200px] rounded-full"></div>
      </div>

      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="text-5xl font-extrabold text-center text-[#d4af37] mb-16 tracking-wide drop-shadow-[0_2px_10px_rgba(212,175,55,0.4)]"
      >
        Alege frizerul tƒÉu preferat
      </motion.h2>

      <div className="relative max-w-7xl mx-auto grid sm:grid-cols-2 lg:grid-cols-3 gap-12 px-6 z-10">
        {barbers.map((barber) => (
          <motion.div
            key={barber.id}
            whileHover={{ scale: 1.07, rotateY: 5 }}
            transition={{ type: "spring", stiffness: 300 }}
            className="group bg-gradient-to-br from-[#111] to-[#1f1f1f] rounded-3xl border border-[#d4af37]/40 shadow-[0_0_20px_rgba(212,175,55,0.15)] hover:shadow-[0_0_35px_rgba(212,175,55,0.35)] overflow-hidden relative transform-gpu transition-all duration-300"
          >
            {/* ‚úÇÔ∏è imagine + efect overlay */}
            <div className="relative">
              <img
                src={`http://localhost/barbershop/frontend/backend/uploads/${barber.imagine || "default.jpg"}`}
                alt={barber.nume}
                className="w-full h-72 object-cover group-hover:scale-110 transition-transform duration-700"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent"></div>
              <div className="absolute bottom-4 left-0 right-0 text-center px-6">
                <h3 className="text-3xl font-semibold text-[#d4af37] tracking-wide drop-shadow-md">
                  {barber.nume}
                </h3>
                <p className="text-gray-300 italic mt-1 text-sm">
                  {barber.specializare}
                </p>
              </div>
            </div>

            {/* üí¨ descriere + buton */}
            <div className="p-6 text-center relative z-10">
              <p className="text-gray-400 text-sm mb-6 leading-relaxed">
                Cu o experien»õƒÉ remarcabilƒÉ »ôi aten»õie la detalii, {barber.nume} √Æ»õi garanteazƒÉ un look impecabil.
              </p>

              <motion.button
                whileHover={{ scale: 1.08 }}
                whileTap={{ scale: 0.96 }}
                onClick={() => handleSelectBarber(barber)}
                className="relative z-20 w-full py-3 bg-[#d4af37] text-black font-semibold rounded-xl hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition-all duration-300 shadow-[0_0_15px_rgba(212,175,55,0.4)] group-hover:shadow-[0_0_25px_rgba(212,175,55,0.8)]"
              >
                ProgrameazƒÉ-te la mine
              </motion.button>
            </div>

            {/* ‚ú® efecte de lumini subtile */}
            <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition duration-700">
              <div className="absolute w-full h-full bg-gradient-to-tr from-transparent via-[#d4af37]/5 to-transparent animate-pulse"></div>
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
