import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import Services from "./pages/Services";
import Booking from "./pages/Booking";
import About from "./pages/About";
import Contact from "./pages/Contact";
import Dashboard from "./pages/Dashboard";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Profile from "./pages/Profile";
import Barbers from "./pages/Barbers";

// âœ… verificÄƒ dacÄƒ e admin
const AdminRoute = ({ children }) => {
  const rawUser =
    localStorage.getItem("loggedUser") || sessionStorage.getItem("loggedUser");

  if (!rawUser || rawUser === "undefined" || rawUser === "null") return <Navigate to="/" />;

  let user;
  try {
    user = JSON.parse(rawUser);
  } catch {
    return <Navigate to="/" />;
  }

  if (user && user.isAdmin) return children;
  return <Navigate to="/" />;
};

export default function App() {
  return (
    <Router>
      {/* ðŸŸ¢ scroll global È™i navbar fix */}
      <div className="relative min-h-screen bg-black text-white">
        <Navbar />

        {/* ðŸŸ¢ ConÈ›inut scrollabil cu padding sus ca navbarul sÄƒ nu acopere textul */}
        <main className="pt-20">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/services" element={<Services />} />
            <Route path="/barbers" element={<Barbers />} />
            <Route path="/booking" element={<Booking />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/profile" element={<Profile />} />

            {/* ðŸ”’ doar adminul poate accesa dashboardul */}
            <Route
              path="/dashboard"
              element={
                <AdminRoute>
                  <Dashboard />
                </AdminRoute>
              }
            />
          </Routes>
        </main>

        <Footer />
      </div>
    </Router>
  );
}
