import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [telefon, setTelefon] = useState("");
  const [error, setError] = useState("");
  const [remember, setRemember] = useState(false);
  const [isAdminPhone, setIsAdminPhone] = useState(false);
  const [adminCode, setAdminCode] = useState("");
  const navigate = useNavigate();

  // ðŸ”¸ Lista completÄƒ de administratori (cu coduri individuale)
  const adminList = [
    { phone: "+37360000000", name: "Denis", code: "denis123" },
    { phone: "+37376784211", name: "Danu", code: "danu123" },
  ];

  // âœ… DacÄƒ userul e deja logat, Ã®l redirecÈ›ionÄƒm automat
  useEffect(() => {
    const savedUser =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    if (savedUser) {
      window.dispatchEvent(new Event("userUpdated"));
      navigate(savedUser.isAdmin ? "/dashboard" : "/");
    }
  }, [navigate]);

  // ðŸ”Ž VerificÄƒm dacÄƒ telefonul introdus aparÈ›ine unui admin
  useEffect(() => {
    const normalized = telefon.replace(/\s|-/g, "");
    const foundAdmin = adminList.find(
      (a) =>
        a.phone.endsWith(normalized) ||
        normalized.endsWith(a.phone.slice(-8))
    );
    setIsAdminPhone(!!foundAdmin);
  }, [telefon]);

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!telefon.trim()) {
      setError("Introdu numÄƒrul de telefon!");
      return;
    }

    // verificare cod de acces dacÄƒ e admin
    const matchedAdmin = adminList.find(
      (a) => a.phone === telefon || telefon.endsWith(a.phone.slice(-8))
    );

    if (matchedAdmin && adminCode !== matchedAdmin.code) {
      setError(`Cod de acces incorect pentru ${matchedAdmin.name}!`);
      return;
    }

    const formData = new FormData();
    formData.append("telefon", telefon);

    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/login_client.php",
        {
          method: "POST",
          body: formData,
        }
      );

      const data = await res.json();

      if (data.success) {
        const user = data.user;

        const matchedAdmin = adminList.find(
          (a) =>
            a.phone === user.telefon ||
            user.telefon.endsWith(a.phone.slice(-8))
        );

        const isAdmin = !!matchedAdmin;
        const loggedUser = {
          ...user,
          isAdmin,
          adminName: matchedAdmin ? matchedAdmin.name : null,
        };

        if (remember) {
          localStorage.setItem("loggedUser", JSON.stringify(loggedUser));
        } else {
          sessionStorage.setItem("loggedUser", JSON.stringify(loggedUser));
        }

        window.dispatchEvent(new Event("userUpdated"));

        alert(
          matchedAdmin
            ? `Salut ${matchedAdmin.name}, bine ai revenit ca administrator!`
            : `Bun venit, ${user.prenume} ${user.nume}!`
        );

        navigate(isAdmin ? "/dashboard" : "/");
      } else {
        setError(data.error || "NumÄƒr de telefon inexistent!");
      }
    } catch (err) {
      console.error(err);
      setError("Eroare la conexiunea cu serverul.");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/40 backdrop-blur-md border border-[#d4af37]/30 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          Autentificare Client
        </h2>

        <form onSubmit={handleLogin} className="space-y-6">
          {/* Telefon */}
          <div>
            <label className="block text-sm text-gray-300 mb-2">
              NumÄƒr de telefon
            </label>
            <input
              type="tel"
              value={telefon}
              onChange={(e) => {
                setTelefon(e.target.value);
                setError("");
              }}
              placeholder="076784211 sau +37376784211"
              pattern="^(\+373\d{8}|0\d{8})$"
              title="FoloseÈ™te formatul 076784211 sau +37376784211"
              className={`w-full bg-transparent border ${
                error
                  ? "border-red-500 focus:border-red-500"
                  : "border-[#d4af37]/30 focus:border-[#d4af37]"
              } text-white rounded-md px-4 py-2 focus:outline-none transition`}
            />
          </div>

          {/* ðŸ”‘ Cod de acces (vizibil doar pentru admin) */}
          {isAdminPhone && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <label className="block text-sm text-gray-300 mb-2">
                Cod de acces admin
              </label>
              <input
                type="password"
                value={adminCode}
                onChange={(e) => setAdminCode(e.target.value)}
                placeholder="Introdu codul secret"
                className={`w-full bg-transparent border ${
                  error
                    ? "border-red-500 focus:border-red-500"
                    : "border-[#d4af37]/30 focus:border-[#d4af37]"
                } text-white rounded-md px-4 py-2 focus:outline-none transition`}
              />
            </motion.div>
          )}

          {error && <p className="text-red-500 text-xs">{error}</p>}

          {/* Remember me */}
          <div className="flex items-center justify-between text-sm mt-3">
            <label className="flex items-center gap-2 cursor-pointer select-none">
              <input
                type="checkbox"
                checked={remember}
                onChange={() => setRemember(!remember)}
                className="accent-[#d4af37] w-4 h-4"
              />
              <span className="text-gray-300">Èšine-mÄƒ minte</span>
            </label>

            <a
              href="/register"
              className="text-[#d4af37] hover:underline transition"
            >
              ÃŽnregistreazÄƒ-te
            </a>
          </div>

          {/* Buton Login */}
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-5 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition duration-300"
          >
            AutentificÄƒ-te
          </motion.button>
        </form>

        <p className="text-center text-gray-400 text-xs mt-8">
          Â© 2025 Denis Barbershop. Toate drepturile rezervate.
        </p>
      </motion.div>
    </section>
  );
}
