import { useState, useEffect } from "react";

export default function Profile() {
  const [user, setUser] = useState(null);
  const [numeNou, setNumeNou] = useState("");

  // La Ã®ncÄƒrcare, luÄƒm userul curent
  useEffect(() => {
    const userSalvat =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    setUser(userSalvat);
    if (userSalvat?.prenume) setNumeNou(userSalvat.prenume);
  }, []);

  // ðŸ”„ FuncÈ›ie de salvare a modificÄƒrilor
  const handleSave = () => {
    if (!user) return;

    const userActualizat = { ...user, prenume: numeNou };

    // SalvÄƒm Ã®n localStorage
    localStorage.setItem("loggedUser", JSON.stringify(userActualizat));

    // âœ… AnunÈ›Äƒm toate componentele (Navbar inclus)
    window.dispatchEvent(new Event("userUpdated"));

    // actualizÄƒm È™i starea localÄƒ
    setUser(userActualizat);

    alert("Numele a fost actualizat cu succes!");
  };

  if (!user) return <div className="text-center mt-10">Nu eÈ™ti autentificat!</div>;

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-gray-100">
      <div className="bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-md border border-gray-700">
        <h2 className="text-2xl font-semibold text-[#d4af37] mb-6 text-center">
          Profil utilizator
        </h2>

        <div className="space-y-4">
          <div>
            <label className="block text-sm text-gray-400 mb-1">Prenume:</label>
            <input
              type="text"
              value={numeNou}
              onChange={(e) => setNumeNou(e.target.value)}
              className="w-full px-3 py-2 rounded-md bg-gray-700 text-gray-100 focus:outline-none focus:ring-2 focus:ring-[#d4af37]"
            />
          </div>

          <div>
            <label className="block text-sm text-gray-400 mb-1">Telefon:</label>
            <input
              type="text"
              value={user.telefon || ""}
              disabled
              className="w-full px-3 py-2 rounded-md bg-gray-700 text-gray-400 cursor-not-allowed"
            />
          </div>

          <button
            onClick={handleSave}
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-2 rounded-md hover:bg-[#e5c04a] transition"
          >
            SalveazÄƒ modificÄƒrile
          </button>
        </div>
      </div>
    </div>
  );
}
