import { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { CalendarDays, Clock } from "lucide-react";

export default function Booking() {
  const [form, setForm] = useState({
    nume: "",
    prenume: "",
    telefon: "",
    service: "",
    date: "",
    time: "",
  });

  const dateRef = useRef(null);

  // genereazƒÉ orele din 40 √Æn 40 minute √Æntre 08:00 - 20:00
  const generateTimes = () => {
    const times = [];
    let start = 8 * 60;
    const end = 20 * 60;
    while (start <= end) {
      const h = Math.floor(start / 60).toString().padStart(2, "0");
      const m = (start % 60).toString().padStart(2, "0");
      times.push(`${h}:${m}`);
      start += 40;
    }
    return times;
  };
  const timeSlots = generateTimes();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("loggedUser"));
    if (user) {
      setForm((prev) => ({
        ...prev,
        nume: user.nume || "",
        prenume: user.prenume || "",
        telefon: user.telefon || "",
      }));
    }
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!form.date || !form.time || !form.service) {
      alert("CompleteazƒÉ toate c√¢mpurile!");
      return;
    }

    // üóìÔ∏è Validare: sƒÉ nu po»õi alege o datƒÉ din trecut
    const selectedDate = new Date(form.date + "T" + form.time);
    const now = new Date();
    if (selectedDate < now) {
      alert("‚ùå Nu po»õi alege o datƒÉ sau orƒÉ din trecut!");
      return;
    }

    // ‚úÖ convertim data √Æn DD/MM/YYYY
    const [year, month, day] = form.date.split("-");
    const formattedDate = `${day}/${month}/${year}`;

    const bookings = JSON.parse(localStorage.getItem("bookings") || "[]");
    const newBooking = {
      nume: form.nume,
      prenume: form.prenume,
      telefon: form.telefon,
      service: form.service,
      date: formattedDate, // format corect
      time: form.time,
    };

    bookings.push(newBooking);
    localStorage.setItem("bookings", JSON.stringify(bookings));

    alert(`‚úÖ Programarea pentru ${formattedDate}, ora ${form.time}, a fost salvatƒÉ!`);

    setForm((prev) => ({
      ...prev,
      service: "",
      date: "",
      time: "",
    }));
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/50 backdrop-blur-md border border-[#d4af37]/40 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          ProgrameazƒÉ-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* üîí date luate din profil */}
          <input
            type="text"
            name="nume"
            value={form.nume}
            disabled
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-[#b8a96f] cursor-not-allowed select-none"
          />
          <input
            type="text"
            name="prenume"
            value={form.prenume}
            disabled
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-[#b8a96f] cursor-not-allowed select-none"
          />
          <input
            type="tel"
            name="telefon"
            value={form.telefon}
            disabled
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-[#b8a96f] cursor-not-allowed select-none"
          />

          {/* üß¥ select servicii */}
          <select
            name="service"
            value={form.service}
            onChange={handleChange}
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:outline-none focus:border-[#d4af37] hover:border-[#d4af37] transition"
            required
          >
            <option value="">‚Äî selecteazƒÉ serviciul ‚Äî</option>
            <option value="Tuns" className="bg-[#1a1a1a] text-[#d4af37]">Tuns</option>
            <option value="Tuns + BarbƒÉ" className="bg-[#1a1a1a] text-[#d4af37]">Tuns + BarbƒÉ</option>
            <option value="BarbƒÉ" className="bg-[#1a1a1a] text-[#d4af37]">BarbƒÉ</option>
            <option value="SpƒÉlat + Coafat" className="bg-[#1a1a1a] text-[#d4af37]">SpƒÉlat + Coafat</option>
          </select>

          {/* üìÖ Data + ‚è∞ Ora */}
          <div className="flex gap-3">
            {/* Data */}
            <div className="relative flex-1 flex items-center">
              <input
                ref={dateRef}
                type="date"
                name="date"
                value={form.date}
                min={new Date().toISOString().split("T")[0]} // nu po»õi selecta trecutul
                onChange={handleChange}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md pr-10 pl-3 py-2 text-gray-200 focus:border-[#d4af37] transition appearance-none [&::-webkit-calendar-picker-indicator]:hidden"
                required
              />
              <button
                type="button"
                onClick={() => dateRef.current.showPicker()}
                className="absolute right-3 text-[#d4af37] hover:text-yellow-400 transition"
              >
                <CalendarDays className="w-5 h-5" />
              </button>
            </div>

            {/* Ora */}
            <div className="relative flex-1 flex items-center">
              <select
                name="time"
                value={form.time}
                onChange={handleChange}
                className="w-full appearance-none bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-3 py-2 text-gray-200 focus:border-[#d4af37] transition"
                required
              >
                <option value="">HH:MM</option>
                {timeSlots.map((t) => (
                  <option key={t} value={t} className="bg-[#1a1a1a] text-[#d4af37]">
                    {t}
                  </option>
                ))}
              </select>
              <Clock className="absolute right-3 text-[#d4af37] w-5 h-5 pointer-events-none" />
            </div>
          </div>

          {/* Buton */}
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            ProgrameazƒÉ-te acum
          </motion.button>
        </form>
      </motion.div>
    </section>
  );
}
