import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

export default function Barbers() {
  const [barbers, setBarbers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost/barbershop/frontend/backend/api/get_barbers.php")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setBarbers(data);
      })
      .catch(() => setBarbers([]));
  }, []);

  const handleSelectBarber = (barber) => {
    // Salvăm temporar barberul ales în localStorage
    localStorage.setItem("selectedBarber", JSON.stringify(barber));
    navigate("/booking");
  };

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white py-20">
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="text-4xl font-bold text-center text-[#d4af37] mb-12"
      >
        Alege frizerul tău preferat
      </motion.h2>

      <div className="max-w-6xl mx-auto grid sm:grid-cols-2 lg:grid-cols-3 gap-10 px-6">
        {barbers.map((barber) => (
          <motion.div
            key={barber.id}
            whileHover={{ scale: 1.05 }}
            className="bg-black/60 border border-[#d4af37]/30 rounded-2xl overflow-hidden shadow-lg text-center backdrop-blur-lg"
          >
            <div className="relative">
              <img
                src={`http://localhost/barbershop/frontend/backend/uploads/${barber.imagine || "default.jpg"}`}
                alt={barber.nume}
                className="w-full h-64 object-cover"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent"></div>
            </div>

            <div className="p-6">
              <h3 className="text-2xl font-semibold text-[#d4af37]">
                {barber.nume}
              </h3>
              <p className="text-gray-400 mt-2">{barber.specializare}</p>

              <button
                onClick={() => handleSelectBarber(barber)}
                className="mt-6 w-full py-2 bg-[#d4af37] text-black font-semibold rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
              >
                Programează-te la mine
              </button>
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
