import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { CalendarDays } from "lucide-react";
import DatePicker, { registerLocale } from "react-datepicker";
import { ro } from "date-fns/locale";
import "react-datepicker/dist/react-datepicker.css";

registerLocale("ro", ro);

export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [search, setSearch] = useState("");
  const [holidays, setHolidays] = useState([]);
  const [selectedHoliday, setSelectedHoliday] = useState(null);
  const [forcedBarberId, setForcedBarberId] = useState(null);

  const activeBarberId = forcedBarberId || user?.id || null;

  // --- ob»õinerea programƒÉrilor
  const fetchAppointments = async (barberId) => {
    if (!barberId) return setAppointments([]);
    try {
      const res = await fetch(
        `http://localhost/barbershop/backend/api/get_barber_appointments.php?barber_id=${encodeURIComponent(
          barberId
        )}`,
        { cache: "no-store" }
      );
      const data = await res.json();
      if (data?.success && Array.isArray(data.appointments)) {
        const sorted = data.appointments.sort(
          (a, b) =>
            new Date(`${a.date}T${a.time}`) - new Date(`${b.date}T${b.time}`)
        );
        setAppointments(sorted);
      } else setAppointments([]);
    } catch {
      setAppointments([]);
    }
  };

  // --- ob»õinerea zilelor libere
  const fetchHolidays = async (barberId) => {
    if (!barberId) return setHolidays([]);
    try {
      const res = await fetch(
        `http://localhost/barbershop/backend/api/get_holidays.php?barber_id=${encodeURIComponent(
          barberId
        )}`,
        { cache: "no-store" }
      );
      const data = await res.json();
      if (data?.success)
        setHolidays(
          data.holidays
            .map((h) => (typeof h === "string" ? h : h?.date || null))
            .filter(Boolean)
        );
      else setHolidays([]);
    } catch {
      setHolidays([]);
    }
  };

  // --- ini»õializare utilizator
  useEffect(() => {
    const raw =
      localStorage.getItem("loggedUser") || sessionStorage.getItem("loggedUser");
    let stored = null;
    try {
      stored = raw ? JSON.parse(raw) : null;
    } catch {
      stored = null;
    }

    if (!stored) return;

    setUser(stored);
    const phoneNormalized = (stored.telefon || "").replace(/\D/g, "");

    if (phoneNormalized === "060000000") {
      setForcedBarberId(1);
      fetchAppointments(1);
      fetchHolidays(1);
    } else if (phoneNormalized === "076784211") {
      setForcedBarberId(2);
      fetchAppointments(2);
      fetchHolidays(2);
    } else if (stored.id) {
      fetchAppointments(stored.id);
      fetchHolidays(stored.id);
    }
  }, []);

  // --- editare/salvare programare
  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("‚úÖ Programarea a fost actualizatƒÉ!");
        setEditingId(null);
        if (activeBarberId) fetchAppointments(activeBarberId);
      } else setMessage("‚ö† Eroare la actualizare!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- »ôtergere programare
  const handleDelete = async (id) => {
    if (!window.confirm("E»ôti sigur cƒÉ vrei sƒÉ »ôtergi aceastƒÉ programare?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setAppointments((prev) => prev.filter((a) => a.id !== id));
        setMessage("üóëÔ∏è Programarea a fost »ôtearsƒÉ!");
      } else setMessage("‚ö† Eroare la »ôtergere!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- adƒÉugare zi liberƒÉ (cu fix de fus orar)
  const handleAddHoliday = async () => {
    if (!selectedHoliday) return setMessage("‚ö† SelecteazƒÉ o datƒÉ din calendar!");
    if (!activeBarberId) return setMessage("‚ö† Barber inexistent pentru salvare!");

    const localDate = new Date(
      selectedHoliday.getTime() - selectedHoliday.getTimezoneOffset() * 60000
    );
    const dateStr = localDate.toISOString().split("T")[0];

    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/set_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: activeBarberId, date: dateStr }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üå¥ Ziua liberƒÉ a fost adƒÉugatƒÉ corect!");
        fetchHolidays(activeBarberId);
        setSelectedHoliday(null);
      } else setMessage(data.message || "‚ö† Eroare la salvare!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- »ôtergere zi liberƒÉ
  const handleDeleteHoliday = async (date) => {
    if (!activeBarberId) return setMessage("‚ö† Barber inexistent pentru »ôtergere!");
    if (!window.confirm("»òtergi aceastƒÉ zi liberƒÉ?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: activeBarberId, date }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üóìÔ∏è Ziua liberƒÉ a fost eliminatƒÉ!");
        fetchHolidays(activeBarberId);
      } else setMessage("‚ö† Eroare la »ôtergere!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- filtrare programƒÉri
  const filteredAppointments = appointments.filter((a) => {
    const normalize = (str) =>
      (str || "").toLowerCase().replace(/\s+/g, "").trim();
    const term = normalize(search);
    const client = normalize(a.client_nume || a.nume || "");
    const phone = normalize(a.client_telefon || a.telefon || "");
    const service = normalize(a.service || "");
    return (
      client.includes(term) || phone.includes(term) || service.includes(term)
    );
  });

  if (!user?.id)
    return (
      <section className="bg-[#0f0f0f] text-white p-6 flex justify-center items-center min-h-[60vh]">
        <p className="text-center text-red-400 text-sm sm:text-base">
          Nu e»ôti autentificat. Te rog sƒÉ te reconectezi pentru a accesa panoul.
        </p>
      </section>
    );

  return (
    <section className="bg-[#0f0f0f] text-white px-3 py-6 md:px-6 lg:px-10 flex justify-center">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="w-full max-w-6xl bg-black/40 border border-[#d4af37]/40 rounded-2xl p-4 md:p-6 lg:p-8 shadow-2xl overflow-hidden pb-28"
      >
        <h1 className="text-xl md:text-2xl lg:text-3xl font-bold text-[#d4af37] mb-6 text-center">
          Dashboard ‚Äî {user?.prenume || "Frizer"}{" "}
          {forcedBarberId ? `(barber ${forcedBarberId})` : ""}
        </h1>

        {/* üå¥ Calendar concedii */}
        <div className="mb-8 bg-[#1a1a1a]/60 border border-[#d4af37]/30 rounded-xl p-4 md:p-6">
          <h2 className="text-lg md:text-xl font-semibold text-[#d4af37] mb-4 text-center">
            Zile libere / Concediu
          </h2>

          <div className="flex flex-col md:flex-row items-center justify-center gap-3 md:gap-4">
            <div className="relative w-full md:w-auto">
              <DatePicker
                selected={selectedHoliday}
                onChange={(date) => setSelectedHoliday(date)}
                locale="ro"
                dateFormat="dd.MM.yyyy"
                calendarStartDay={1}
                minDate={new Date()}
                placeholderText="Alege o datƒÉ..."
                className="w-full bg-[#0f0f0f] border border-[#d4af37]/60 rounded-lg px-4 py-2 md:px-5 md:py-3 text-[#d4af37] focus:outline-none focus:ring-2 focus:ring-[#d4af37]/40 text-sm md:text-base placeholder-gray-500"
                calendarClassName="!bg-[#0f0f0f] !border !border-[#d4af37]/40 !rounded-xl !shadow-[0_0_20px_rgba(212,175,55,0.25)] !text-white"
                dayClassName={(date) => {
                  const dateStr = date.toISOString().split("T")[0];
                  if (holidays.includes(dateStr)) {
                    return "bg-[#b91c1c]/70 text-red-200 font-semibold rounded-md";
                  }
                  return "text-[#d4af37] hover:bg-[#d4af37]/25 hover:text-black font-medium transition rounded-md";
                }}
                weekDayClassName={() => "text-[#f5e6a0] font-semibold uppercase"}
                popperClassName="!z-50"
              />
              <CalendarDays className="absolute right-3 top-2.5 text-[#d4af37] w-5 h-5 pointer-events-none" />
            </div>

            <button
              onClick={handleAddHoliday}
              className="bg-[#d4af37] text-black font-semibold px-5 md:px-6 py-2 md:py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition-all duration-300 shadow-md w-full md:w-auto text-sm md:text-base"
            >
              SalveazƒÉ concediul
            </button>
          </div>

          {holidays.length > 0 && (
            <ul className="mt-5 text-center text-xs md:text-sm text-gray-300 flex flex-wrap justify-center gap-2 md:gap-3">
              {holidays.map((h, i) => (
                <li
                  key={i}
                  className="bg-[#222]/60 px-2 md:px-3 py-1.5 rounded-md flex items-center gap-1 md:gap-2"
                >
                  <span>{h}</span>
                  <button
                    onClick={() => handleDeleteHoliday(h)}
                    className="text-red-400 hover:text-red-500 text-base leading-none"
                  >
                    ‚úï
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* üîç CƒÉutare */}
        <div className="flex justify-center mb-6">
          <input
            type="text"
            placeholder="CautƒÉ client, serviciu sau telefon..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full max-w-md bg-[#1a1a1a] border border-[#d4af37]/40 text-[#d4af37] placeholder-gray-500 rounded-md px-4 py-2 md:px-5 md:py-3 text-sm md:text-base focus:border-[#d4af37] focus:outline-none"
          />
        </div>

        {/* üìÖ Lista programƒÉrilor */}
        {filteredAppointments.length === 0 ? (
          <p className="text-center text-gray-400 text-sm md:text-base mb-10">
            Nu s-au gƒÉsit programƒÉri pentru termenul introdus.
          </p>
        ) : (
          <div className="overflow-y-auto max-h-[65vh] rounded-lg border border-[#d4af37]/30 shadow-inner mb-10">
            <table className="w-full text-[11px] sm:text-sm md:text-base border-collapse">
              <thead className="bg-[#1a1a1a] text-[#d4af37] sticky top-0 z-10">
                <tr>
                  <th className="p-2 md:p-4">#</th>
                  <th className="p-2 md:p-4">Client</th>
                  <th className="p-2 md:p-4">Telefon</th>
                  <th className="p-2 md:p-4">Serviciu</th>
                  <th className="p-2 md:p-4">Data</th>
                  <th className="p-2 md:p-4">Ora</th>
                  <th className="p-2 md:p-4">Ac»õiuni</th>
                </tr>
              </thead>
              <tbody>
                {filteredAppointments.map((a, i) => (
                  <tr
                    key={a.id}
                    className="hover:bg-[#d4af37]/10 transition text-center"
                  >
                    <td className="p-2 md:p-4">{i + 1}</td>
                    <td className="p-2 md:p-4">
                      {a.client_prenume} {a.client_nume}
                    </td>
                    <td className="p-2 md:p-4">{a.client_telefon}</td>
                    <td className="p-2 md:p-4">{a.service}</td>
                    <td className="p-2 md:p-4">{a.date}</td>
                    <td className="p-2 md:p-4">{a.time}</td>
                    <td className="p-2 md:p-4 flex flex-col md:flex-row justify-center gap-1 md:gap-3">
                      <button
                        onClick={() => handleEdit(a)}
                        className="text-[#d4af37] hover:underline text-sm md:text-base"
                      >
                        EditeazƒÉ
                      </button>
                      <button
                        onClick={() => handleDelete(a.id)}
                        className="text-red-500 hover:underline text-sm md:text-base"
                      >
                        »òterge
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </motion.div>
    </section>
  );
}
