import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [bookings, setBookings] = useState([]);

  // Funcție care curăță automat programările expirate
  const cleanExpiredBookings = () => {
    const storedBookings = JSON.parse(localStorage.getItem("bookings") || "[]");
    const now = new Date();

    const validBookings = storedBookings.filter((b) => {
      if (!b.date || !b.time) return false;
      const bookingTime = new Date(`${b.date}T${b.time}`);
      const diff = (now - bookingTime) / 1000 / 60;
      return diff < 15; // păstrăm doar cele mai noi de 15 minute
    });

    localStorage.setItem("bookings", JSON.stringify(validBookings));
    setBookings(validBookings);
  };

  // Rulăm curățarea la fiecare 1 minut
  useEffect(() => {
    cleanExpiredBookings();
    const interval = setInterval(cleanExpiredBookings, 60000);
    return () => clearInterval(interval);
  }, []);

  const handleDelete = (index) => {
    const updated = [...bookings];
    updated.splice(index, 1);
    setBookings(updated);
    localStorage.setItem("bookings", JSON.stringify(updated));
  };

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white py-12">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-5xl mx-auto px-6"
      >
        <h1 className="text-3xl md:text-4xl font-bold text-center text-[#d4af37] mb-10">
          Programări — Panou Admin
        </h1>

        {bookings.length === 0 ? (
          <p className="text-center text-gray-400">Nu există programări active.</p>
        ) : (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {bookings.map((b, i) => (
              <div
                key={i}
                className="bg-black/50 border border-[#d4af37]/30 rounded-lg p-6 shadow-lg relative"
              >
                <p><strong>Client:</strong> {b.prenume} {b.nume}</p>
                <p><strong>Telefon:</strong> {b.telefon}</p>
                <p><strong>Serviciu:</strong> {b.service}</p>
                <p><strong>Data:</strong> {b.date}</p>
                <p><strong>Ora:</strong> {b.time}</p>

                <button
                  onClick={() => handleDelete(i)}
                  className="absolute bottom-3 right-4 text-red-500 hover:underline text-sm"
                >
                  Șterge
                </button>
              </div>
            ))}
          </div>
        )}
      </motion.div>
    </section>
  );
}
