import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    // simulăm extragerea din localStorage
    const saved = JSON.parse(localStorage.getItem("bookings") || "[]");

    // sortăm după dată (cele mai apropiate primele)
    const sorted = saved.sort(
      (a, b) => new Date(a.data) - new Date(b.data)
    );

    setBookings(sorted);
  }, []);

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white py-12">
      <div className="max-w-5xl mx-auto px-6">
        <h2 className="text-4xl font-bold text-[#d4af37] mb-10 text-center">
          Programări — Panou Admin
        </h2>

        {bookings.length === 0 ? (
          <p className="text-center text-gray-400">
            Nu există programări momentan.
          </p>
        ) : (
          <div className="grid md:grid-cols-2 gap-6">
            {bookings.map((booking, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3, delay: index * 0.05 }}
                className="bg-black/40 border border-[#d4af37]/30 rounded-xl p-6 shadow-lg hover:border-[#d4af37] transition"
              >
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-[#d4af37]">
                    {booking.nume} {booking.prenume}
                  </h3>
                  <span className="text-sm text-gray-400">
                    #{index + 1}
                  </span>
                </div>

                <div className="space-y-1 text-sm">
                  <p>
                    <span className="text-gray-400">Telefon:</span>{" "}
                    {booking.telefon}
                  </p>
                  <p>
                    <span className="text-gray-400">Serviciu:</span>{" "}
                    {booking.serviciu}
                  </p>
                  <p>
                    <span className="text-gray-400">Data:</span>{" "}
                    <span className="text-white font-medium">
                      {new Date(booking.data).toLocaleDateString("ro-RO", {
                        weekday: "long",
                        year: "numeric",
                        month: "long",
                        day: "numeric",
                      })}
                    </span>
                  </p>
                  <p>
                    <span className="text-gray-400">Ora:</span>{" "}
                    {booking.ora}
                  </p>
                </div>

                <div className="flex justify-end mt-4">
                  <button
                    onClick={() => {
                      const confirmDelete = window.confirm(
                        `Ștergi programarea lui ${booking.prenume}?`
                      );
                      if (!confirmDelete) return;

                      const updated = bookings.filter((_, i) => i !== index);
                      setBookings(updated);
                      localStorage.setItem(
                        "bookings",
                        JSON.stringify(updated)
                      );
                    }}
                    className="text-red-400 hover:text-red-600 text-sm font-medium transition"
                  >
                    Șterge
                  </button>
                </div>
              </motion.div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
}
