const handleRegister = async (e) => {
  e.preventDefault();

  if (!nume.trim() || !prenume.trim() || !telefon.trim()) {
    setMessage("CompleteazÄƒ toate cÃ¢mpurile!");
    return;
  }

  const formData = new FormData();
  formData.append("nume", nume);
  formData.append("prenume", prenume);
  formData.append("telefon", telefon);

  try {
    const res = await fetch(
      "http://localhost/barbershop/backend/api/register_client.php",
      {
        method: "POST",
        body: formData, // ðŸ”¹ nu mai folosim JSON.stringify
      }
    );

    const data = await res.json();
    if (data.success) {
      setMessage("Cont creat cu succes!");
      setTimeout(() => navigate("/login"), 1500);
    } else {
      setMessage(data.error || "A apÄƒrut o eroare la Ã®nregistrare.");
    }
  } catch (err) {
    console.error(err);
    setMessage("Eroare la conexiunea cu serverul.");
  }
};
