import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { CalendarDays } from "lucide-react";
import DatePicker, { registerLocale } from "react-datepicker";
import { ro } from "date-fns/locale";
import "react-datepicker/dist/react-datepicker.css";

registerLocale("ro", ro);

export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [search, setSearch] = useState("");
  const [holidays, setHolidays] = useState([]);
  const [selectedHoliday, setSelectedHoliday] = useState(null);

  // --- ob»õinerea programƒÉrilor
  const fetchAppointments = async (barberId) => {
    if (!barberId) return setAppointments([]);
    try {
      const res = await fetch(
        `http://localhost/barbershop/backend/api/get_barber_appointments.php?barber_id=${barberId}`,
        { cache: "no-store" }
      );
      const data = await res.json();
      if (data?.success && Array.isArray(data.appointments)) {
        const sorted = data.appointments.sort(
          (a, b) =>
            new Date(`${a.date}T${a.time}`) - new Date(`${b.date}T${b.time}`)
        );
        setAppointments(sorted);
      } else setAppointments([]);
    } catch {
      setAppointments([]);
    }
  };

  // --- ob»õinerea zilelor libere
  const fetchHolidays = async (barberId) => {
    if (!barberId) return setHolidays([]);
    try {
      const res = await fetch(
        `http://localhost/barbershop/backend/api/get_holidays.php?barber_id=${barberId}`,
        { cache: "no-store" }
      );
      const data = await res.json();
      if (data?.success)
        setHolidays(
          data.holidays
            .map((h) => (typeof h === "string" ? h : h?.date || null))
            .filter(Boolean)
        );
      else setHolidays([]);
    } catch {
      setHolidays([]);
    }
  };

  // --- ini»õializare utilizator
  useEffect(() => {
    const raw =
      localStorage.getItem("loggedUser") || sessionStorage.getItem("loggedUser");
    let stored = null;
    try {
      stored = raw ? JSON.parse(raw) : null;
    } catch {
      stored = null;
    }

    if (!stored?.id) return;
    setUser(stored);
    fetchAppointments(stored.id);
    fetchHolidays(stored.id);
  }, []);

  // --- func»õii editare/salvare
  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("‚úÖ Programarea a fost actualizatƒÉ!");
        setEditingId(null);
        if (user) fetchAppointments(user.id);
      } else setMessage("‚ö† Eroare la actualizare!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- »ôtergere programare
  const handleDelete = async (id) => {
    if (!window.confirm("E»ôti sigur cƒÉ vrei sƒÉ »ôtergi aceastƒÉ programare?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setAppointments((prev) => prev.filter((a) => a.id !== id));
        setMessage("üóëÔ∏è Programarea a fost »ôtearsƒÉ!");
      } else setMessage("‚ö† Eroare la »ôtergere!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- adƒÉugare zi liberƒÉ
  const handleAddHoliday = async () => {
    if (!selectedHoliday) return setMessage("‚ö† SelecteazƒÉ o datƒÉ din calendar!");
    const dateStr = selectedHoliday.toISOString().split("T")[0];
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/set_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date: dateStr }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üå¥ Ziua liberƒÉ a fost adƒÉugatƒÉ!");
        fetchHolidays(user.id);
        setSelectedHoliday(null);
      } else setMessage("‚ö† Eroare la salvare!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- »ôtergere zi liberƒÉ
  const handleDeleteHoliday = async (date) => {
    if (!window.confirm("»òtergi aceastƒÉ zi liberƒÉ?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üóìÔ∏è Ziua liberƒÉ a fost eliminatƒÉ!");
        fetchHolidays(user.id);
      } else setMessage("‚ö† Eroare la »ôtergere!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // --- curƒÉ»õare programƒÉri vechi
  const handleCleanupOldAppointments = async () => {
    if (!window.confirm("»òtergi toate programƒÉrile vechi?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_old_appointments.php"
      );
      const data = await res.json();
      if (data.success) {
        setMessage(`üßπ ${data.message} (»òterse: ${data.deleted_count})`);
        if (user) fetchAppointments(user.id);
      } else setMessage("‚ö† Eroare la curƒÉ»õare!");
    } catch {
      setMessage("‚ö† Eroare la conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 3000);
    }
  };

  // --- filtrare programƒÉri
  const filteredAppointments = appointments.filter((a) => {
    const normalize = (str) =>
      (str || "").toLowerCase().replace(/\s+/g, "").trim();
    const term = normalize(search);
    const client = normalize(a.client_nume || a.nume || "");
    const phone = normalize(a.client_telefon || a.telefon || "");
    const service = normalize(a.service || "");
    return (
      client.includes(term) || phone.includes(term) || service.includes(term)
    );
  });

  if (!user?.id)
    return (
      <section className="bg-[#0f0f0f] text-white p-6 flex justify-center items-center min-h-[60vh]">
        <p className="text-center text-red-400 text-sm sm:text-base">
          Nu e»ôti autentificat. Te rog sƒÉ te reconectezi pentru a accesa panoul.
        </p>
      </section>
    );

  return (
    <section className="bg-[#0f0f0f] text-white p-4 sm:p-8 flex justify-center">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="w-full max-w-6xl bg-black/40 border border-[#d4af37]/40 rounded-2xl p-5 sm:p-8 shadow-2xl overflow-y-auto"
      >
        <h1 className="text-2xl sm:text-3xl font-bold text-[#d4af37] mb-6 text-center">
          Dashboard ‚Äî {user?.prenume || "Frizer"}
        </h1>

        {/* üßπ CurƒÉ»õare programƒÉri vechi */}
        <div className="flex justify-center mb-6">
          <button
            onClick={handleCleanupOldAppointments}
            className="bg-red-600 hover:bg-red-700 text-white font-semibold px-6 py-2 rounded-md shadow-lg border border-red-400 hover:border-red-500 transition-all duration-300"
          >
            üßπ »òterge programƒÉrile vechi
          </button>
        </div>

        {/* üå¥ Zile libere / Concediu */}
        <div className="mb-8 bg-[#1a1a1a]/60 border border-[#d4af37]/30 rounded-xl p-5">
          <h2 className="text-lg sm:text-xl font-semibold text-[#d4af37] mb-4 text-center">
            Zile libere / Concediu
          </h2>

          <div className="flex flex-col sm:flex-row items-center justify-center gap-3">
            <div className="relative w-full sm:w-auto">
              <DatePicker
                selected={selectedHoliday}
                onChange={(date) => setSelectedHoliday(date)}
                locale="ro"
                dateFormat="dd.MM.yyyy"
                calendarStartDay={1}
                minDate={new Date()}
                highlightDates={[
                  {
                    "react-datepicker__day--highlighted-custom-1": holidays.map(
                      (d) => new Date(d)
                    ),
                  },
                ]}
                placeholderText="Alege o datƒÉ..."
                className="w-full bg-[#0f0f0f] border border-[#d4af37]/50 rounded-lg px-4 py-2 pr-10 text-[#d4af37] focus:border-[#d4af37] focus:outline-none focus:ring-2 focus:ring-[#d4af37]/50 transition text-base cursor-pointer placeholder-gray-500"
                calendarClassName="!bg-[#0f0f0f] !border !border-[#d4af37]/40 !rounded-xl !shadow-2xl !text-white"
                dayClassName={(date) =>
                  holidays.includes(date.toISOString().split('T')[0])
                    ? "bg-red-600/60 text-white rounded-md"
                    : "hover:bg-[#d4af37]/20 transition rounded-md"
                }
              />
              <CalendarDays className="absolute right-3 top-2.5 text-[#d4af37] w-5 h-5 pointer-events-none" />
            </div>

            <button
              onClick={handleAddHoliday}
              className="bg-[#d4af37] text-black font-semibold px-6 py-2 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition-all duration-300 shadow-md w-full sm:w-auto"
            >
              SalveazƒÉ concediul
            </button>
          </div>

          {holidays.length > 0 && (
            <ul className="mt-5 text-center text-sm text-gray-300 flex flex-wrap justify-center gap-2">
              {holidays.map((h, i) => (
                <li
                  key={i}
                  className="bg-[#222]/60 px-3 py-1.5 rounded-md flex items-center gap-2"
                >
                  <span>{h}</span>
                  <button
                    onClick={() => handleDeleteHoliday(h)}
                    className="text-red-400 hover:text-red-500 text-lg leading-none"
                  >
                    ‚úï
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* üîç CƒÉutare */}
        <div className="flex justify-center mb-6">
          <input
            type="text"
            placeholder="CautƒÉ client, serviciu sau telefon..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full max-w-md bg-[#1a1a1a] border border-[#d4af37]/40 text-[#d4af37] placeholder-gray-500 rounded-md px-4 py-2 focus:border-[#d4af37] focus:outline-none text-sm"
          />
        </div>

        {/* üí¨ Mesaj */}
        {message && (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className={`text-center mb-5 font-medium ${
              message.includes("‚úÖ") || message.includes("üå¥") || message.includes("üóìÔ∏è")
                ? "text-green-400"
                : "text-red-400"
            }`}
          >
            {message}
          </motion.p>
        )}

        {/* üìÖ Lista programƒÉrilor */}
        {filteredAppointments.length === 0 ? (
          <p className="text-center text-gray-400 text-sm">
            Nu s-au gƒÉsit programƒÉri pentru termenul introdus.
          </p>
        ) : (
          <div className="overflow-x-auto rounded-lg border border-[#d4af37]/30 shadow-inner">
            <table className="w-full text-[11px] sm:text-sm border-collapse">
              <thead className="bg-[#1a1a1a] text-[#d4af37] sticky top-0">
                <tr>
                  <th className="p-3">#</th>
                  <th className="p-3">Client</th>
                  <th className="p-3">Telefon</th>
                  <th className="p-3">Serviciu</th>
                  <th className="p-3">Data</th>
                  <th className="p-3">Ora</th>
                  <th className="p-3">Ac»õiuni</th>
                </tr>
              </thead>
              <tbody>
                {filteredAppointments.map((a, i) => (
                  <tr
                    key={a.id}
                    className="hover:bg-[#d4af37]/10 transition text-center"
                  >
                    <td className="p-2">{i + 1}</td>
                    <td className="p-2">
                      {a.client_prenume} {a.client_nume}
                    </td>
                    <td className="p-2">{a.client_telefon}</td>
                    <td className="p-2">{a.service}</td>
                    <td className="p-2">
                      {editingId === a.id ? (
                        <input
                          type="date"
                          min={new Date().toISOString().split("T")[0]}
                          value={edited.date}
                          onChange={(e) =>
                            setEdited((prev) => ({ ...prev, date: e.target.value }))
                          }
                          className="bg-[#111] border border-[#d4af37]/50 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        <span>{a.date}</span>
                      )}
                    </td>
                    <td className="p-2">
                      {editingId === a.id ? (
                        <input
                          type="time"
                          value={edited.time}
                          onChange={(e) =>
                            setEdited((prev) => ({ ...prev, time: e.target.value }))
                          }
                          className="bg-[#111] border border-[#d4af37]/50 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        <span>{a.time}</span>
                      )}
                    </td>
                    <td className="p-2 flex flex-col sm:flex-row justify-center gap-1 sm:gap-2">
                      {editingId === a.id ? (
                        <button
                          onClick={() => handleSave(a.id)}
                          className="bg-[#d4af37] text-black px-3 py-1 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37]"
                        >
                          SalveazƒÉ
                        </button>
                      ) : (
                        <>
                          <button
                            onClick={() => handleEdit(a)}
                            className="text-[#d4af37] hover:underline"
                          >
                            EditeazƒÉ
                          </button>
                          <button
                            onClick={() => handleDelete(a.id)}
                            className="text-red-500 hover:underline"
                          >
                            »òterge
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </motion.div>
    </section>
  );
}
