import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [search, setSearch] = useState("");
  const [holidays, setHolidays] = useState([]);
  const [selectedHoliday, setSelectedHoliday] = useState("");

  // --- fetch appointments
  const fetchAppointments = async (barberId) => {
    if (!barberId) return setAppointments([]);
    try {
      const res = await fetch(
        `http://localhost/barbershop/backend/api/get_barber_appointments.php?barber_id=${barberId}`,
        { cache: "no-store" }
      );
      const data = await res.json();
      if (data?.success && Array.isArray(data.appointments)) {
        const sorted = data.appointments.sort(
          (a, b) =>
            new Date(`${a.date}T${a.time}`) - new Date(`${b.date}T${b.time}`)
        );
        setAppointments(sorted);
      } else setAppointments([]);
    } catch {
      setAppointments([]);
    }
  };

  // --- fetch holidays
  const fetchHolidays = async (barberId) => {
    if (!barberId) return setHolidays([]);
    try {
      const res = await fetch(
        `http://localhost/barbershop/backend/api/get_holidays.php?barber_id=${barberId}`,
        { cache: "no-store" }
      );
      const data = await res.json();
      if (data?.success)
        setHolidays(
          data.holidays
            .map((h) => (typeof h === "string" ? h : h?.date || null))
            .filter(Boolean)
        );
      else setHolidays([]);
    } catch {
      setHolidays([]);
    }
  };

  // --- initial
  useEffect(() => {
    const raw =
      localStorage.getItem("loggedUser") || sessionStorage.getItem("loggedUser");
    let stored = null;
    try {
      stored = raw ? JSON.parse(raw) : null;
    } catch {
      stored = null;
    }

    if (!stored?.id) return;

    setUser(stored);
    fetchAppointments(stored.id);
    fetchHolidays(stored.id);
  }, []);

  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("âœ… Programarea actualizatÄƒ!");
        setEditingId(null);
        if (user) fetchAppointments(user.id);
      } else setMessage("âš  Eroare la actualizare!");
    } catch {
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("È˜tergi aceastÄƒ programare?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setAppointments((prev) => prev.filter((a) => a.id !== id));
        setMessage("ðŸ—‘ï¸ Programarea È™tearsÄƒ!");
      } else setMessage("âš  Eroare la È™tergere!");
    } catch {
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleAddHoliday = async () => {
    if (!selectedHoliday) return setMessage("âš  Alege o datÄƒ!");
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/set_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date: selectedHoliday }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("ðŸŒ´ Zi liberÄƒ adÄƒugatÄƒ!");
        fetchHolidays(user.id);
        setSelectedHoliday("");
      } else setMessage(data.error || "âš  Eroare la salvare!");
    } catch {
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleDeleteHoliday = async (date) => {
    if (!window.confirm("È˜tergi aceastÄƒ zi liberÄƒ?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("ðŸ—“ï¸ Ziua liberÄƒ eliminatÄƒ!");
        fetchHolidays(user.id);
      } else setMessage("âš  Eroare la È™tergere!");
    } catch {
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleCleanupOldAppointments = async () => {
    if (
      !window.confirm("Sigur vrei sÄƒ È™tergi toate programÄƒrile din zilele trecute?")
    )
      return;

    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_old_appointments.php"
      );
      const data = await res.json();
      if (data.success) {
        setMessage(`ðŸ§¹ ${data.message} (È˜terse: ${data.deleted_count})`);
        if (user) fetchAppointments(user.id);
      } else setMessage("âš  Eroare la curÄƒÈ›are!");
    } catch {
      setMessage("âš  Eroare la conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 3000);
    }
  };

  const filteredAppointments = appointments.filter((a) => {
    const normalize = (str) =>
      (str || "").toLowerCase().replace(/\s+/g, "").trim();
    const term = normalize(search);
    const client = normalize(a.client_nume || a.nume || "");
    const phone = normalize(a.client_telefon || a.telefon || "");
    const service = normalize(a.service || "");
    const barber = normalize(a.barber_name || a.barber_prenume || "");
    return (
      client.includes(term) ||
      phone.includes(term) ||
      service.includes(term) ||
      barber.includes(term)
    );
  });

  if (!user?.id)
    return (
      <section className="bg-[#0f0f0f] text-white p-6 flex justify-center items-center min-h-[60vh]">
        <p className="text-center text-red-400 text-sm sm:text-base">
          Nu eÈ™ti autentificat corect. Re-autentificÄƒ-te pentru a vedea dashboard-ul.
        </p>
      </section>
    );

  return (
    <section className="bg-[#0f0f0f] text-white p-4 sm:p-8 flex justify-center">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="w-full max-w-6xl bg-black/40 border border-[#d4af37]/40 rounded-2xl p-4 sm:p-8 shadow-lg overflow-y-auto"
      >
        <h1 className="text-2xl sm:text-3xl font-bold text-[#d4af37] mb-6 text-center">
          Dashboard â€” {user?.prenume || "Barber"}
        </h1>

        {/* ðŸ§¹ Buton È™tergere programÄƒri vechi */}
        <div className="flex justify-center mb-6">
          <button
            onClick={handleCleanupOldAppointments}
            className="bg-red-600 hover:bg-red-700 text-white font-semibold px-5 py-2 rounded-md transition border border-red-400 hover:border-red-500 text-sm sm:text-base"
          >
            ðŸ§¹ È˜terge programÄƒrile vechi
          </button>
        </div>

        {/* ðŸŒ´ Zile libere */}
        <div className="mb-8 bg-[#1a1a1a]/50 border border-[#d4af37]/30 rounded-xl p-4">
