import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const navigate = useNavigate();

useEffect(() => {
  const user =
    JSON.parse(localStorage.getItem("loggedUser")) ||
    JSON.parse(sessionStorage.getItem("loggedUser"));

  if (!user?.isAdmin) {
    navigate("/admin-login");
  }
}, [navigate]);


export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [holidays, setHolidays] = useState([]);
  const [selectedHoliday, setSelectedHoliday] = useState("");

  // üîπ PreluƒÉm programƒÉrile din backend
  const fetchAppointments = async () => {
    try {
      const storedUser =
        JSON.parse(localStorage.getItem("loggedUser")) ||
        JSON.parse(sessionStorage.getItem("loggedUser"));

      if (!storedUser) {
        setAppointments([]);
        setLoading(false);
        setMessage("‚ö†Ô∏è Nu e»ôti logat ca frizer.");
        return;
      }

      const barberId = storedUser.barber_id || storedUser.id;

      const res = await fetch(
        `http://localhost/barbershop/frontend/backend/api/get_all_appointments.php?barber_id=${barberId}`
      );
      const data = await res.json();

      if (Array.isArray(data)) {
        data.sort(
          (a, b) =>
            new Date(`${a.date}T${a.time}`) -
            new Date(`${b.date}T${b.time}`)
        );
        setAppointments(data);
      } else {
        setAppointments([]);
      }

      setLoading(false);
    } catch (err) {
      console.error(err);
      setAppointments([]);
      setMessage("‚ùå Eroare la conexiunea cu serverul.");
      setLoading(false);
    }
  };

  // üîπ PreluƒÉm concediile din backend
  const fetchHolidays = async () => {
    try {
      const storedUser =
        JSON.parse(localStorage.getItem("loggedUser")) ||
        JSON.parse(sessionStorage.getItem("loggedUser"));
      if (!storedUser) return;

      const barberId = storedUser.barber_id || storedUser.id;

      const res = await fetch(
        `http://localhost/barbershop/frontend/backend/api/get_holidays.php?barber_id=${barberId}`
      );
      const data = await res.json();
      if (data.success) setHolidays(data.holidays);
    } catch (err) {
      console.error("Eroare holidays:", err);
    }
  };

  useEffect(() => {
    fetchAppointments();
    fetchHolidays();
  }, []);

  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();

      if (data.success) {
        setMessage("‚úî Programarea a fost actualizatƒÉ!");
        setEditingId(null);
        fetchAppointments();
      } else {
        setMessage("‚ö† Eroare la actualizare!");
      }
    } catch (err) {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üóëÔ∏è »òtergere programare
  const handleDelete = async (id) => {
    if (!window.confirm("Sigur vrei sƒÉ »ôtergi aceastƒÉ programare?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/delete_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setAppointments((prev) => prev.filter((a) => a.id !== id));
        setMessage("üóëÔ∏è Programarea a fost »ôtearsƒÉ!");
      } else setMessage("‚ö† Eroare la »ôtergere!");
    } catch (err) {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üå¥ AdaugƒÉ zi liberƒÉ
  const handleAddHoliday = async () => {
    if (!selectedHoliday) {
      setMessage("‚ö† SelecteazƒÉ o datƒÉ pentru concediu!");
      return;
    }

    const storedUser =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    if (!storedUser) return;

    const barberId = storedUser.barber_id || storedUser.id;

    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/set_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: barberId, date: selectedHoliday }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üå¥ Ziua liberƒÉ a fost adƒÉugatƒÉ!");
        fetchHolidays();
      } else {
        setMessage(data.error || "‚ö† Eroare la salvare!");
      }
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üå¥ »òterge o zi liberƒÉ
  const handleDeleteHoliday = async (date) => {
    const storedUser =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    if (!storedUser) return;
    const barberId = storedUser.barber_id || storedUser.id;

    if (!window.confirm("»òtergi aceastƒÉ zi liberƒÉ?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/delete_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: barberId, date }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üóìÔ∏è Ziua liberƒÉ a fost eliminatƒÉ!");
        fetchHolidays();
      } else setMessage("‚ö† Eroare la »ôtergere!");
    } catch {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üîç Filtrare dupƒÉ nume / telefon
  const filteredAppointments = appointments.filter((a) => {
    const term = search.toLowerCase().trim();
    return (
      a.nume?.toLowerCase().includes(term) ||
      a.telefon?.includes(term) ||
      a.service?.toLowerCase().includes(term)
    );
  });

  if (loading)
    return (
      <div className="flex items-center justify-center min-h-screen bg-[#0f0f0f] text-[#d4af37] text-xl font-semibold">
        Se √ÆncarcƒÉ programƒÉrile...
      </div>
    );

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white p-8">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-6xl mx-auto bg-black/40 border border-[#d4af37]/40 rounded-2xl p-8 shadow-lg"
      >
        <h1 className="text-3xl font-bold text-[#d4af37] mb-6 text-center">
          Dashboard ‚Äî ProgramƒÉrile tale
        </h1>

        {/* üîç CƒÉutare */}
        <div className="flex justify-center mb-6">
          <input
            type="text"
            placeholder="CautƒÉ client sau serviciu..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full max-w-md bg-[#1a1a1a] border border-[#d4af37]/40 text-[#d4af37] placeholder-gray-500 rounded-md px-4 py-2 focus:border-[#d4af37] focus:outline-none"
          />
        </div>

        {/* üå¥ Concedii */}
        <div className="mb-8 bg-[#1a1a1a]/50 border border-[#d4af37]/30 rounded-xl p-5">
          <h2 className="text-xl font-semibold text-[#d4af37] mb-3 text-center">
            Zile libere / Concediu
          </h2>

          <div className="flex flex-col sm:flex-row items-center justify-center gap-3">
            <input
              type="date"
              value={selectedHoliday}
              onChange={(e) => setSelectedHoliday(e.target.value)}
              min={new Date().toISOString().split("T")[0]}
              className="bg-[#111] border border-[#d4af37]/50 rounded-md px-3 py-2 text-[#d4af37] focus:border-[#d4af37] focus:outline-none"
            />
            <button
              onClick={handleAddHoliday}
              className="bg-[#d4af37] text-black font-semibold px-5 py-2 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
            >
              SalveazƒÉ concediul
            </button>
          </div>

          {holidays.length > 0 && (
            <ul className="mt-4 text-center text-sm text-gray-300">
              {holidays.map((h, i) => (
                <li key={i} className="mb-2">
                  {h.date}{" "}
                  <button
                    onClick={() => handleDeleteHoliday(h.date)}
                    className="text-red-500 hover:underline ml-2"
                  >
                    »òterge
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        {message && (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center text-[#d4af37] mb-4 font-medium"
          >
            {message}
          </motion.p>
        )}

        {/* üìÖ Tabel */}
        {filteredAppointments.length === 0 ? (
          <p className="text-center text-gray-400">Nu existƒÉ programƒÉri.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full text-sm border-collapse">
              <thead className="bg-[#1a1a1a] text-[#d4af37] sticky top-0">
                <tr>
                  <th className="p-3 border border-[#d4af37]/30">#</th>
                  <th className="p-3 border border-[#d4af37]/30">Client</th>
                  <th className="p-3 border border-[#d4af37]/30">Telefon</th>
                  <th className="p-3 border border-[#d4af37]/30">Serviciu</th>
                  <th className="p-3 border border-[#d4af37]/30">Data</th>
                  <th className="p-3 border border-[#d4af37]/30">Ora</th>
                  <th className="p-3 border border-[#d4af37]/30">Sursa</th>
                  <th className="p-3 border border-[#d4af37]/30">Ac»õiuni</th>
                </tr>
              </thead>
              <tbody>
                {filteredAppointments.map((a, i) => (
                  <tr
                    key={`${a.id}-${i}`}
                    className="hover:bg-[#d4af37]/10 transition text-center"
                  >
                    <td className="p-2 border border-[#d4af37]/20">{i + 1}</td>
                    <td className="p-2 border border-[#d4af37]/20">{a.nume}</td>
                    <td className="p-2 border border-[#d4af37]/20">{a.telefon}</td>
                    <td className="p-2 border border-[#d4af37]/20">{a.service}</td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="date"
                          value={edited.date}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              date: e.target.value,
                            }))
                          }
                          className="bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        a.date
                      )}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="time"
                          value={edited.time}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              time: e.target.value,
                            }))
                          }
                          className="bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        a.time
                      )}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20 italic text-sm">
                      {a.client_id ? "Cont client" : "Client rapid"}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <button
                          onClick={() => handleSave(a.id)}
                          className="bg-[#d4af37] text-black px-3 py-1 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
                        >
                          SalveazƒÉ
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(a)}
                          className="text-[#d4af37] hover:underline"
                        >
                          EditeazƒÉ
                        </button>
                      )}
                      <button
                        onClick={() => handleDelete(a.id)}
                        className="ml-2 text-red-500 hover:underline"
                      >
                        »òterge
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </motion.div> 
    </section>
  );
}
