import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

export default function AdminLogin() {
  const [form, setForm] = useState({ telefon: "", parola: "" });
  const [error, setError] = useState("");     // ✅ adăugăm starea pentru eroare
  const [message, setMessage] = useState(""); // ✅ adăugăm și mesaj de succes
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");

    const { telefon, parola } = form;
    if (!telefon || !parola) {
      setError("Completează toate câmpurile!");
      return;
    }

    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/admin_login.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ telefon, parola }),
        }
      );

      const data = await res.json();

      if (data.success) {
        localStorage.setItem("loggedUser", JSON.stringify(data.user));
        window.dispatchEvent(new Event("userUpdated"));
        setMessage("✅ Logare reușită!");
        setTimeout(() => navigate("/dashboard"), 800);
      } else {
        setError(data.error || "Eroare la logare!");
      }
    } catch (err) {
      setError("Eroare la conexiunea cu serverul!");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/60 border border-[#d4af37]/40 rounded-2xl p-8 w-full max-w-sm shadow-lg"
      >
        <h2 className="text-3xl font-bold text-[#d4af37] mb-6 text-center">
          Login Administrator
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          <input
            type="text"
            name="telefon"
            placeholder="Telefon"
            value={form.telefon}
            onChange={handleChange}
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition"
          />

          <input
            type="password"
            name="parola"
            placeholder="Parolă"
            value={form.parola}
            onChange={handleChange}
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition"
          />

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full bg-[#d4af37] text-black font-semibold py-2 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            Autentificare
          </motion.button>
        </form>

        {error && (
          <p className="text-red-400 text-center mt-4 text-sm">{error}</p>
        )}
        {message && (
          <p className="text-green-400 text-center mt-4 text-sm">{message}</p>
        )}
      </motion.div>
    </section>
  );
}
