import { motion } from "framer-motion";
import bgImage from "../assets/barbershop.jpg";
import Footer from "../components/Footer";
import { Phone, MapPin, Clock } from "lucide-react";

export default function Home() {
  // üî∏ VerificƒÉm dacƒÉ salonul e deschis (program 8:00‚Äì20:00)
  const hour = new Date().getHours();
  const isOpen = hour >= 8 && hour < 20;

  return (
    <section
      className="min-h-screen flex flex-col items-center justify-center text-center relative text-white overflow-hidden"
      style={{
        backgroundImage: `url(${bgImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* Fundal cu blur cinematic */}
      <div
        className="absolute inset-0 bg-black/60 backdrop-blur-[3px]"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          filter: "blur(8px) brightness(0.6)",
          transform: "scale(1.05)", // evitƒÉ marginile neclare
        }}
      ></div>

      {/* Overlay gradient elegant */}
      <div className="absolute inset-0 bg-gradient-to-b from-black/40 via-black/70 to-black/90"></div>

      {/* Con»õinut principal */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="relative z-10 px-6"
      >
        <h1 className="text-4xl sm:text-5xl md:text-6xl font-extrabold text-[#d4af37] mb-6 drop-shadow-[0_4px_8px_rgba(0,0,0,0.8)]">
          Barbershop de clasƒÉ pentru Gentlemen adevƒÉra»õi
        </h1>

        <p className="text-gray-200 text-lg max-w-2xl mx-auto mb-8 drop-shadow-[0_2px_4px_rgba(0,0,0,0.6)]">
          ProgrameazƒÉ-te »ôi experimenteazƒÉ rafinamentul »ôi precizia unui look perfect.
        </p>

        <motion.a
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          href="/booking"
          className="inline-block bg-[#d4af37] text-black font-semibold py-3 px-6 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition duration-300 shadow-lg hover:shadow-[#d4af37]/30"
        >
          ProgrameazƒÉ-te acum
        </motion.a>
      </motion.div>

      {/* üî• Sec»õiune informativƒÉ elegantƒÉ adƒÉugatƒÉ (fƒÉrƒÉ sƒÉ modificƒÉm footerul tƒÉu) */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="relative z-10 mt-12 mb-6 text-gray-300 text-sm"
      >
        <div className="flex flex-col md:flex-row items-center justify-center gap-6">
          {/* üìç AdresƒÉ */}
          <div className="flex items-center gap-2">
            <MapPin size={18} className="text-[#d4af37]" />
            <span>Chi»ôinƒÉu, str. BƒÉrba»õilor nr. 7</span>
          </div>

          {/* üïì Program */}
          <div className="flex items-center gap-2">
            <Clock size={18} className="text-[#d4af37]" />
            <motion.span
              animate={{ opacity: [1, 0.6, 1] }}
              transition={{ duration: 2, repeat: Infinity }}
              className={`font-semibold ${
                isOpen ? "text-green-400" : "text-red-500"
              }`}
            >
              {isOpen ? "Deschis acum" : "√énchis - deschidem la 8:00"}
            </motion.span>
          </div>

          {/* üìû Telefon */}
          <a
            href="tel:+37376784211"
            className="flex items-center gap-2 hover:text-[#d4af37] transition"
          >
            <Phone size={18} className="text-[#d4af37]" />
            <span>+373 767 842 11</span>
          </a>
        </div>
      </motion.div>

      {/* üëá Footerul tƒÉu original, intact */}
      <Footer />
    </section>
  );
}
