import { useState } from "react";
import { motion } from "framer-motion";

export default function Booking() {
  const today = new Date();
  const [selectedDate, setSelectedDate] = useState(today.toISOString().split("T")[0]);
  const [bookings, setBookings] = useState({});
  const [name, setName] = useState("");

  // Orele disponibile pentru fiecare zi
  const hours = ["10:00", "11:00", "12:00", "13:00", "14:00", "15:00", "16:00"];

  // Functie care procesează rezervarea
  const handleBooking = (hour) => {
    if (!name.trim()) return alert("Introdu numele înainte de a rezerva!");

    const newBooking = {
      name,
      date: selectedDate,
      hour,
    };

    // Salvăm în state local
    setBookings((prev) => ({
      ...prev,
      [selectedDate]: [...(prev[selectedDate] || []), hour],
    }));

    // Salvăm și în localStorage
    const allBookings = JSON.parse(localStorage.getItem("bookings") || "[]");
    allBookings.push(newBooking);
    localStorage.setItem("bookings", JSON.stringify(allBookings));

    alert(`Rezervare efectuată pentru ${name} la ora ${hour}.`);
  };

  // Funcție care generează următoarele 7 zile
  const getNextDays = (count = 7) => {
    const days = [];
    for (let i = 0; i < count; i++) {
      const d = new Date(today);
      d.setDate(today.getDate() + i);
      days.push(d.toISOString().split("T")[0]);
    }
    return days;
  };

  // --- return principal ---
  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white py-20 px-6">
      <div className="max-w-4xl mx-auto">
        <motion.h1
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold text-center text-[#d4af37] mb-12"
        >
          Alege ziua și ora pentru programare
        </motion.h1>

        {/* Formular pentru nume */}
        <div className="max-w-md mx-auto mb-10">
          <label className="block text-gray-300 mb-2 text-left">Nume complet</label>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            type="text"
            placeholder="Ex: Denis Popescu"
            className="w-full bg-transparent border border-[#d4af37]/30 text-white rounded-md px-4 py-2 focus:outline-none focus:border-[#d4af37] transition"
          />
        </div>

        {/* Selectare zi */}
        <div className="flex flex-wrap justify-center gap-3 mb-10">
          {getNextDays().map((date) => (
            <button
              key={date}
              onClick={() => setSelectedDate(date)}
              className={`px-4 py-2 rounded-md border ${
                selectedDate === date
                  ? "bg-[#d4af37] text-black font-semibold"
                  : "border-[#d4af37]/40 text-gray-300 hover:border-[#d4af37]"
              } transition`}
            >
              {new Date(date).toLocaleDateString("ro-RO", {
                weekday: "short",
                day: "2-digit",
                month: "2-digit",
              })}
            </button>
          ))}
        </div>

        {/* Ore disponibile */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
          {hours.map((hour) => {
            const isTaken = bookings[selectedDate]?.includes(hour);
            return (
              <motion.button
                key={hour}
                whileHover={{ scale: isTaken ? 1 : 1.05 }}
                whileTap={{ scale: isTaken ? 1 : 0.95 }}
                disabled={isTaken}
                onClick={() => handleBooking(hour)}
                className={`py-3 rounded-md border transition font-medium ${
                  isTaken
                    ? "bg-gray-700 text-gray-400 cursor-not-allowed"
                    : "border-[#d4af37]/50 text-[#d4af37] hover:bg-[#d4af37] hover:text-black"
                }`}
              >
                {hour}
              </motion.button>
            );
          })}
        </div>
      </div>
    </section>
  );
}
