import { useState } from "react";
import { motion } from "framer-motion";
import { Lock, User } from "lucide-react";
import { useNavigate } from "react-router-dom";

export default function AdminLogin() {
  const [form, setForm] = useState({ telefon: "", parola: "" });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    const { telefon, parola } = form;
    if (!telefon || !parola) {
      setMessage("⚠ Completează toate câmpurile!");
      return;
    }

    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/admin_login.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ telefon, parola }),
        }
      );

      const data = await res.json();

      if (data.success) {
        localStorage.setItem("loggedUser", JSON.stringify(data.user));
        window.dispatchEvent(new Event("userUpdated"));
        setMessage("✅ Autentificare reușită! Redirect...");
        setTimeout(() => navigate("/dashboard"), 1500);
      } else {
        setMessage(data.error || "❌ Date invalide!");
      }
    } catch {
      setMessage("❌ Eroare la conexiunea cu serverul!");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/50 border border-[#d4af37]/40 rounded-2xl p-10 w-full max-w-md shadow-lg backdrop-blur-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-8">
          Login Administrator
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          {/* Telefon */}
          <div className="relative">
            <User className="absolute left-3 top-3 text-[#d4af37]" />
            <input
              type="text"
              name="telefon"
              placeholder="Telefon admin"
              value={form.telefon}
              onChange={handleChange}
              className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-10 py-2 text-gray-200 focus:border-[#d4af37] transition"
            />
          </div>

          {/* Parola */}
          <div className="relative">
            <Lock className="absolute left-3 top-3 text-[#d4af37]" />
            <input
              type="password"
              name="parola"
              placeholder="Parolă"
              value={form.parola}
              onChange={handleChange}
              className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-10 py-2 text-gray-200 focus:border-[#d4af37] transition"
            />
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            Autentificare
          </motion.button>
        </form>

        {message && (
          <p
            className={`mt-5 text-center text-sm ${
              message.includes("✅")
                ? "text-green-400"
                : message.includes("❌")
                ? "text-red-400"
                : "text-yellow-400"
            }`}
          >
            {message}
          </p>
        )}
      </motion.div>
    </section>
  );
}
