import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { CalendarDays, Clock } from "lucide-react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default function Booking() {
  const [form, setForm] = useState({
    nume: "",
    prenume: "",
    telefon: "",
    service: "",
    date: null,
    time: "",
  });
  const [bookings, setBookings] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);

  // generăm orele între 08:00 - 20:00 din 40 în 40 min
  const generateTimes = () => {
    const times = [];
    let start = 8 * 60;
    const end = 20 * 60;
    while (start <= end) {
      const h = Math.floor(start / 60).toString().padStart(2, "0");
      const m = (start % 60).toString().padStart(2, "0");
      times.push(`${h}:${m}`);
      start += 40;
    }
    return times;
  };
  const timeSlots = generateTimes();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("loggedUser"));
    const all = JSON.parse(localStorage.getItem("bookings") || "[]");
    if (user) {
      setForm((p) => ({
        ...p,
        nume: user.nume,
        prenume: user.prenume,
        telefon: user.telefon,
      }));
    }
    setBookings(all);
  }, []);

  // ore deja ocupate într-o zi
  const getBookedTimes = (dateStr) =>
    bookings
      .filter((b) => b.date === dateStr)
      .map((b) => b.time);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.date || !form.time || !form.service) {
      alert("Completează toate câmpurile!");
      return;
    }

    const dateStr = form.date.toLocaleDateString("en-US"); // MM/DD/YYYY
    const exists = bookings.some(
      (b) => b.date === dateStr && b.time === form.time
    );
    if (exists) {
      alert("❌ Acea oră este deja ocupată!");
      return;
    }

    const updated = [...bookings, { ...form, date: dateStr }];
    localStorage.setItem("bookings", JSON.stringify(updated));
    setBookings(updated);

    alert(`✅ Programarea pentru ${dateStr} la ora ${form.time} a fost salvată!`);
    setForm({ ...form, service: "", date: null, time: "" });
  };

  // colorare zile în calendar
  const dayClassName = (date) => {
    const dateStr = date.toLocaleDateString("en-US");
    const count = bookings.filter((b) => b.date === dateStr).length;
    if (count === 0) return "react-datepicker__day--available-green";
    if (count > 0 && count <= 10)
      return "react-datepicker__day--available-yellow";
    if (count > 10) return "react-datepicker__day--available-red";
  };

  const bookedTimes = selectedDate
    ? getBookedTimes(selectedDate.toLocaleDateString("en-US"))
    : [];

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/50 backdrop-blur-md border border-[#d4af37]/40 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          Programează-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-6">
          <input disabled value={form.nume} className="field" />
          <input disabled value={form.prenume} className="field" />
          <input disabled value={form.telefon} className="field" />

          {/* serviciu */}
          <select
            name="service"
            value={form.service}
            onChange={(e) => setForm({ ...form, service: e.target.value })}
            className="field text-gray-200"
            required
          >
            <option value="">— selectează serviciul —</option>
            <option value="Tuns">Tuns</option>
            <option value="Tuns + Barbă">Tuns + Barbă</option>
            <option value="Barbă">Barbă</option>
            <option value="Spălat + Coafat">Spălat + Coafat</option>
          </select>

          {/* calendar modern */}
          <div className="relative">
            <label className="block text-gray-300 mb-1">Selectează data:</label>
            <CalendarDays className="absolute right-3 top-9 text-[#d4af37] w-5 h-5 pointer-events-none" />
            <DatePicker
              selected={form.date}
              onChange={(d) => {
                setForm({ ...form, date: d });
                setSelectedDate(d);
              }}
              minDate={new Date()}
              dayClassName={dayClassName}
              placeholderText="Alege o dată"
              dateFormat="MM/dd/yyyy"
              className="field pr-10 text-white"
              calendarClassName="custom-calendar"
            />
          </div>

          {/* ore */}
          {selectedDate && (
            <div className="relative flex flex-col">
              <label className="text-sm text-gray-300 mb-1">
                Selectează ora:
              </label>
              <Clock className="absolute right-3 top-8 text-[#d4af37] w-5 h-5 pointer-events-none" />
              <select
                name="time"
                value={form.time}
                onChange={(e) => setForm({ ...form, time: e.target.value })}
                className="field pr-10"
                required
              >
                <option value="">HH:MM</option>
                {timeSlots.map((t) => (
                  <option
                    key={t}
                    value={t}
                    disabled={bookedTimes.includes(t)}
                    className={
                      bookedTimes.includes(t)
                        ? "text-[#c27a7a] bg-[#1a1a1a]"
                        : "text-[#d4af37] bg-[#1a1a1a]"
                    }
                  >
                    {t}
                  </option>
                ))}
              </select>
            </div>
          )}

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            Programează-te acum
          </motion.button>
        </form>
      </motion.div>
    </section>
  );
}
