import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  const fetchAppointments = async () => {
    try {
      const storedUser =
        JSON.parse(localStorage.getItem("loggedUser")) ||
        JSON.parse(sessionStorage.getItem("loggedUser"));

      if (!storedUser) {
        setAppointments([]);
        setLoading(false);
        setMessage("âš ï¸ Nu eÈ™ti logat ca frizer.");
        return;
      }

      // Barber ID fallback (Ã®n unele cazuri Ã®l avem doar ca user.id)
      const barberId = storedUser.barber_id || storedUser.id;

      const res = await fetch(
        `http://localhost/barbershop/frontend/backend/api/get_all_appointments.php?barber_id=${barberId}`
      );
      const data = await res.json();

      if (Array.isArray(data)) {
        data.sort(
          (a, b) =>
            new Date(`${a.date}T${a.time}`) -
            new Date(`${b.date}T${b.time}`)
        );
        setAppointments(data);
      } else {
        setAppointments([]);
      }

      setLoading(false);
    } catch (err) {
      console.error(err);
      setAppointments([]);
      setMessage("âŒ Eroare la conexiunea cu serverul.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();

      if (data.success) {
        setMessage("âœ” Programarea a fost actualizatÄƒ!");
        setTimeout(() => setMessage(""), 2500);
        setEditingId(null);
        fetchAppointments();
      } else {
        setMessage("âš  Eroare la actualizare!");
      }
    } catch (err) {
      setMessage("âš  Eroare de conexiune!");
    }
  };

  if (loading)
    return (
      <div className="flex items-center justify-center min-h-screen bg-[#0f0f0f] text-[#d4af37] text-xl font-semibold">
        Se Ã®ncarcÄƒ programÄƒrile...
      </div>
    );

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white p-8">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-5xl mx-auto bg-black/40 border border-[#d4af37]/40 rounded-2xl p-8 shadow-lg"
      >
        <h1 className="text-3xl font-bold text-[#d4af37] mb-6 text-center">
          Dashboard â€” ProgramÄƒrile tale
        </h1>

        {message && (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center text-[#d4af37] mb-4 font-medium"
          >
            {message}
          </motion.p>
        )}

        {appointments.length === 0 ? (
          <p className="text-center text-gray-400">Nu existÄƒ programÄƒri.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full text-sm border-collapse">
              <thead className="bg-[#1a1a1a] text-[#d4af37]">
                <tr>
                  <th className="p-3 border border-[#d4af37]/30">#</th>
                  <th className="p-3 border border-[#d4af37]/30">Client</th>
                  <th className="p-3 border border-[#d4af37]/30">Telefon</th>
                  <th className="p-3 border border-[#d4af37]/30">Serviciu</th>
                  <th className="p-3 border border-[#d4af37]/30">Data</th>
                  <th className="p-3 border border-[#d4af37]/30">Ora</th>
                  <th className="p-3 border border-[#d4af37]/30">Sursa</th>
                  <th className="p-3 border border-[#d4af37]/30">AcÈ›iuni</th>
                </tr>
              </thead>
              <tbody>
                {appointments.map((a, i) => (
                  <tr
                    key={`${a.id}-${i}`}
                    className="hover:bg-[#d4af37]/10 transition text-center"
                  >
                    <td className="p-2 border border-[#d4af37]/20">{i + 1}</td>
                    <td className="p-2 border border-[#d4af37]/20">{a.nume}</td>
                    <td className="p-2 border border-[#d4af37]/20">{a.telefon}</td>
                    <td className="p-2 border border-[#d4af37]/20">{a.service}</td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="date"
                          value={edited.date}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              date: e.target.value,
                            }))
                          }
                          className="bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        a.date
                      )}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="time"
                          value={edited.time}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              time: e.target.value,
                            }))
                          }
                          className="bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        a.time
                      )}
                    </td>

                    {/* ðŸŸ¡ Sursa programÄƒrii */}
                    <td className="p-2 border border-[#d4af37]/20 text-sm italic">
                      {a.client_id ? "Cont client" : "Client rapid"}
                    </td>

                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <button
                          onClick={() => handleSave(a.id)}
                          className="bg-[#d4af37] text-black px-3 py-1 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
                        >
                          SalveazÄƒ
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(a)}
                          className="text-[#d4af37] hover:underline"
                        >
                          EditeazÄƒ
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </motion.div>
    </section>
  );
}
