import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");

  // üîÑ √éncarcƒÉ programƒÉrile
  const fetchAppointments = async () => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/get_appointments.php"
      );
      const data = await res.json();
      // SorteazƒÉ programƒÉrile dupƒÉ datƒÉ »ôi orƒÉ
      data.sort(
        (a, b) =>
          new Date(`${a.date}T${a.time}`) - new Date(`${b.date}T${b.time}`)
      );
      setAppointments(data);
    } catch {
      setAppointments([]);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  // üß© √éncepem editarea
  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  // üíæ SalvƒÉm modificƒÉrile √Æn backend
  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();

      if (data.success) {
        setMessage("‚úî Programarea a fost actualizatƒÉ!");
        setTimeout(() => setMessage(""), 2500);
        setEditingId(null);
        fetchAppointments();
      } else {
        setMessage("‚ö† Eroare la actualizare!");
      }
    } catch (err) {
      setMessage("‚ö† Eroare de conexiune!");
    }
  };

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white p-8">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-5xl mx-auto bg-black/40 border border-[#d4af37]/40 rounded-2xl p-8 shadow-lg"
      >
        <h1 className="text-3xl font-bold text-[#d4af37] mb-6 text-center">
          Dashboard ‚Äî ProgramƒÉri
        </h1>

        {message && (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center text-[#d4af37] mb-4 font-medium"
          >
            {message}
          </motion.p>
        )}

        {appointments.length === 0 ? (
          <p className="text-center text-gray-400">Nu existƒÉ programƒÉri.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full text-sm border-collapse">
              <thead className="bg-[#1a1a1a] text-[#d4af37]">
                <tr>
                  <th className="p-3 border border-[#d4af37]/30">#</th>
                  <th className="p-3 border border-[#d4af37]/30">Client</th>
                  <th className="p-3 border border-[#d4af37]/30">Telefon</th>
                  <th className="p-3 border border-[#d4af37]/30">Serviciu</th>
                  <th className="p-3 border border-[#d4af37]/30">Data</th>
                  <th className="p-3 border border-[#d4af37]/30">Ora</th>
                  <th className="p-3 border border-[#d4af37]/30">Ac»õiuni</th>
                </tr>
              </thead>
              <tbody>
                {appointments.map((a, i) => (
                  <tr
                    key={a.id}
                    className="hover:bg-[#d4af37]/10 transition text-center"
                  >
                    <td className="p-2 border border-[#d4af37]/20">{i + 1}</td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.nume} {a.prenume}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.telefon}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.service}
                    </td>

                    {/* üóìÔ∏è Data */}
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="date"
                          value={edited.date}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              date: e.target.value,
                            }))
                          }
                          className="bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        a.date
                      )}
                    </td>

                    {/* ‚è∞ Ora */}
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="time"
                          value={edited.time}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              time: e.target.value,
                            }))
                          }
                          className="bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        a.time
                      )}
                    </td>

                    {/* üîò Ac»õiuni */}
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <button
                          onClick={() => handleSave(a.id)}
                          className="bg-[#d4af37] text-black px-3 py-1 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
                        >
                          SalveazƒÉ
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(a)}
                          className="text-[#d4af37] hover:underline"
                        >
                          EditeazƒÉ
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </motion.div>
    </section>
  );
}
