import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { CalendarDays, Clock } from "lucide-react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

export default function Booking() {
  const [form, setForm] = useState({
    nume: "",
    prenume: "",
    telefon: "",
    service: "",
    date: "",
    time: "",
  });
  const [bookings, setBookings] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);

  // GenerƒÉm orele din 40 √Æn 40 minute √Æntre 08:00 - 20:00
  const generateTimes = () => {
    const times = [];
    let start = 8 * 60;
    const end = 20 * 60;
    while (start <= end) {
      const h = Math.floor(start / 60).toString().padStart(2, "0");
      const m = (start % 60).toString().padStart(2, "0");
      times.push(`${h}:${m}`);
      start += 40;
    }
    return times;
  };
  const timeSlots = generateTimes();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("loggedUser"));
    const allBookings = JSON.parse(localStorage.getItem("bookings") || "[]");
    if (user) {
      setForm({
        ...form,
        nume: user.nume,
        prenume: user.prenume,
        telefon: user.telefon,
      });
    }
    setBookings(allBookings);
  }, []);

  // ReturneazƒÉ numƒÉrul de locuri ocupate pentru o datƒÉ
  const countBookingsByDate = (dateStr) => {
    return bookings.filter((b) => b.date === dateStr).length;
  };

  // ReturneazƒÉ orele ocupate pentru data selectatƒÉ
  const getBookedTimes = (dateStr) => {
    return bookings.filter((b) => b.date === dateStr).map((b) => b.time);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!form.date || !form.time || !form.service) {
      alert("CompleteazƒÉ toate c√¢mpurile!");
      return;
    }

    const [year, month, day] = form.date.split("-");
    const formattedDate = `${month}/${day}/${year}`;

    const alreadyBooked = bookings.some(
      (b) => b.date === formattedDate && b.time === form.time
    );
    if (alreadyBooked) {
      alert("‚ùå Ora selectatƒÉ este deja ocupatƒÉ!");
      return;
    }

    const updated = [
      ...bookings,
      { ...form, date: formattedDate },
    ];
    localStorage.setItem("bookings", JSON.stringify(updated));
    setBookings(updated);

    alert(`‚úÖ Programarea pentru ${formattedDate}, ora ${form.time}, a fost salvatƒÉ!`);
    setForm((prev) => ({ ...prev, service: "", date: "", time: "" }));
  };

  const handleDateChange = (date) => {
    const formatted = date.toLocaleDateString("en-US"); // MM/DD/YYYY
    setForm((prev) => ({ ...prev, date: `${date.getFullYear()}-${(date.getMonth() + 1)
      .toString()
      .padStart(2, "0")}-${date.getDate().toString().padStart(2, "0")}` }));
    setSelectedDate(formatted);
  };

  const bookedTimes = selectedDate ? getBookedTimes(selectedDate) : [];

  // Stil personalizat pentru zile √Æn calendar
  const tileClassName = ({ date, view }) => {
    if (view === "month") {
      const formatted = date.toLocaleDateString("en-US");
      const count = countBookingsByDate(formatted);
      if (count === 0) return "available-green";
      if (count > 0 && count <= 10) return "available-yellow";
      if (count > 10) return "available-red";
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/50 backdrop-blur-md border border-[#d4af37]/40 rounded-2xl p-8 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          ProgrameazƒÉ-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* üîí Date luate din profil */}
          <input disabled value={form.nume} className="field" />
          <input disabled value={form.prenume} className="field" />
          <input disabled value={form.telefon} className="field" />

          {/* üß¥ Select servicii */}
          <select
            name="service"
            value={form.service}
            onChange={(e) => setForm({ ...form, service: e.target.value })}
            className="field text-gray-200"
            required
          >
            <option value="">‚Äî selecteazƒÉ serviciul ‚Äî</option>
            <option value="Tuns">Tuns</option>
            <option value="Tuns + BarbƒÉ">Tuns + BarbƒÉ</option>
            <option value="BarbƒÉ">BarbƒÉ</option>
            <option value="SpƒÉlat + Coafat">SpƒÉlat + Coafat</option>
          </select>

          {/* üìÖ Calendar + ‚è∞ Ora */}
          <div className="space-y-4">
            <Calendar
              onChange={handleDateChange}
              tileClassName={tileClassName}
              className="rounded-md bg-[#1a1a1a] text-white border border-[#d4af37]/30"
              minDate={new Date()}
            />

            {/* Ora */}
            {selectedDate && (
              <div className="relative flex flex-col">
                <label className="text-sm text-gray-300 mb-1">SelecteazƒÉ ora:</label>
                <div className="relative">
                  <Clock className="absolute right-3 top-2.5 text-[#d4af37] w-5 h-5 pointer-events-none" />
                  <select
                    name="time"
                    value={form.time}
                    onChange={(e) => setForm({ ...form, time: e.target.value })}
                    className="field appearance-none pr-10"
                    required
                  >
                    <option value="">HH:MM</option>
                    {timeSlots.map((t) => (
                      <option
                        key={t}
                        value={t}
                        disabled={bookedTimes.includes(t)}
                        className={
                          bookedTimes.includes(t)
                            ? "text-[#c27a7a] bg-[#1a1a1a]"
                            : "text-[#d4af37] bg-[#1a1a1a]"
                        }
                      >
                        {t}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            )}
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            ProgrameazƒÉ-te acum
          </motion.button>
        </form>
      </motion.div>
    </section>
  );
}
