import { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { CalendarDays, Clock, Scissors, User, Phone } from "lucide-react";

export default function Booking() {
  const [form, setForm] = useState({
    nume: "",
    telefon: "",
    service: "",
    barber_id: "",
    date: "",
    time: "",
  });

  const [barbers, setBarbers] = useState([]);
  const [bookedTimes, setBookedTimes] = useState([]);
  const [message, setMessage] = useState("");
  const dateRef = useRef(null);

  // Orele între 08:00 și 20:00 din 40 în 40 minute
  const generateTimes = () => {
    const times = [];
    let start = 8 * 60;
    const end = 20 * 60;
    while (start <= end) {
      const h = Math.floor(start / 60).toString().padStart(2, "0");
      const m = (start % 60).toString().padStart(2, "0");
      times.push(`${h}:${m}`);
      start += 40;
    }
    return times;
  };
  const allTimes = generateTimes();

  // Barber selectat din /barbers
  useEffect(() => {
    const selectedBarber = JSON.parse(localStorage.getItem("selectedBarber"));
    if (selectedBarber) {
      setForm((prev) => ({ ...prev, barber_id: selectedBarber.id }));
      localStorage.removeItem("selectedBarber");
    }
  }, []);

  // Încarcă frizerii
  useEffect(() => {
    fetch("http://localhost/barbershop/frontend/backend/api/get_barbers.php")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setBarbers(data);
      })
      .catch(() => setBarbers([]));
  }, []);

  // Orele ocupate
  useEffect(() => {
    if (form.date && form.barber_id) {
      fetch(
        `http://localhost/barbershop/frontend/backend/api/get_booked_times.php?date=${form.date}&barber_id=${form.barber_id}`
      )
        .then((res) => res.json())
        .then((data) => {
          const fixed = data.map((t) => t.slice(0, 5));
          setBookedTimes(fixed);
        })
        .catch(() => setBookedTimes([]));
    } else {
      setBookedTimes([]);
    }
  }, [form.date, form.barber_id]);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    const { nume, telefon, service, barber_id, date, time } = form;
    if (!nume || !telefon || !service || !barber_id || !date || !time) {
      setMessage("Completează toate câmpurile!");
      return;
    }

    const formData = new FormData();
    formData.append("client_id", user?.id || 0);
formData.append("nume", `${user?.prenume || form.nume} ${user?.nume || ""}`);
formData.append("telefon", user?.telefon || form.telefon);
formData.append("service", form.service);
formData.append("date", form.date);
formData.append("time", form.time);
formData.append("barber_id", form.barber_id);


    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/book_appointment.php",
        { method: "POST", body: formData }
      );
      const data = await res.json();

      if (data.success) {
        setMessage("✅ Programarea a fost înregistrată cu succes!");
        setForm({
          nume: "",
          telefon: "",
          service: "",
          barber_id: "",
          date: "",
          time: "",
        });
        setBookedTimes((prev) => [...prev, time]);
      } else {
        setMessage(data.error || "Eroare necunoscută!");
      }
    } catch (err) {
      setMessage("Eroare la conexiunea cu serverul.");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white px-6">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/50 backdrop-blur-md border border-[#d4af37]/40 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          Programează-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          {/* Nume */}
          <div className="relative">
            <input
              type="text"
              name="nume"
              value={form.nume}
              onChange={handleChange}
              placeholder="Numele tău"
              className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition text-base"
              required
            />
            <User className="absolute right-3 top-2.5 text-[#d4af37]" />
          </div>

          {/* Telefon */}
          <div className="relative">
            <input
              type="tel"
              name="telefon"
              value={form.telefon}
              onChange={handleChange}
              placeholder="Numărul de telefon"
              pattern="^(\+373\d{8}|0\d{8})$"
              title="Format: +37360123456 sau 060123456"
              className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition text-base"
              required
            />
            <Phone className="absolute right-3 top-2.5 text-[#d4af37]" />
          </div>

          {/* Serviciu */}
          <select
            name="service"
            value={form.service}
            onChange={handleChange}
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] hover:border-[#d4af37] transition text-base"
            required
          >
            <option value="">— selectează serviciul —</option>
            <option value="Tuns">Tuns</option>
            <option value="Tuns + Barbă">Tuns + Barbă</option>
            <option value="Barbă">Barbă</option>
            <option value="Spălat + Coafat">Spălat + Coafat</option>
          </select>

          {/* Frizer */}
          <div className="relative flex items-center">
            <select
              name="barber_id"
              value={form.barber_id}
              onChange={handleChange}
              className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition text-base appearance-none"
              required
            >
              <option value="">— selectează frizerul —</option>
              {barbers.map((b) => (
                <option key={b.id} value={b.id}>
                  ✂️ {b.nume} — {b.specializare}
                </option>
              ))}
            </select>
            <Scissors className="absolute right-3 text-[#d4af37] w-5 h-5 pointer-events-none" />
          </div>

          {/* Dată + oră */}
          <div className="flex gap-3 items-center justify-between">
            <div className="relative flex-1 flex items-center">
              <input
                ref={dateRef}
                type="date"
                name="date"
                value={form.date}
                onChange={handleChange}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 pr-10 text-gray-200 focus:border-[#d4af37] transition text-base"
                required
              />
              <button
                type="button"
                onClick={() => dateRef.current.showPicker()}
                className="absolute right-3 text-[#d4af37] hover:text-yellow-400 transition"
              >
                <CalendarDays className="w-5 h-5" />
              </button>
            </div>

            <div className="relative flex-1 flex items-center">
              <select
                name="time"
                value={form.time}
                onChange={handleChange}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 pr-10 text-gray-200 focus:border-[#d4af37] transition text-base appearance-none"
                required
              >
                <option value="">HH:MM</option>
                {allTimes.map((t) => {
                  const isBooked = bookedTimes.includes(t);
                  return (
                    <option
                      key={t}
                      value={t}
                      disabled={isBooked}
                      className={
                        isBooked
                          ? "text-gray-500 bg-[#111]/70 cursor-not-allowed"
                          : "text-[#d4af37]"
                      }
                    >
                      {t} {isBooked ? "(ocupat)" : ""}
                    </option>
                  );
                })}
              </select>
              <Clock className="absolute right-3 text-[#d4af37] w-5 h-5 pointer-events-none" />
            </div>
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            Programează-te acum
          </motion.button>
        </form>

        {message && (
          <p
            className={`mt-4 text-center text-sm ${
              message.includes("succes") ? "text-green-400" : "text-red-400"
            }`}
          >
            {message}
          </p>
        )}
      </motion.div>
    </section>
  );
}
