import { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { CalendarDays, Clock, Scissors } from "lucide-react";

export default function Booking() {
  const [form, setForm] = useState({
    nume: "",
    telefon: "",
    service: "",
    barber_id: "",
    date: "",
    time: "",
  });
  const [user, setUser] = useState(null);
  const [barbers, setBarbers] = useState([]);
  const [bookedTimes, setBookedTimes] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [message, setMessage] = useState("");
  const dateRef = useRef(null);

  // üÜï helpers timp
  const SLOT_MIN = 35; // 35 minute / slot

  // üÜï adaugƒÉ minute la "HH:MM"
  const addMinutes = (hhmm, minutes) => {
    if (!hhmm) return null;
    const [h, m] = hhmm.split(":").map(Number);
    const total = h * 60 + m + minutes;
    if (total < 0 || total >= 24 * 60) return null;
    const H = String(Math.floor(total / 60)).padStart(2, "0");
    const M = String(total % 60).padStart(2, "0");
    return `${H}:${M}`;
  };

  // üÜï verificƒÉ pauza 13:00‚Äì13:59
  const inBreak = (hhmm) => hhmm >= "13:00" && hhmm < "14:00";

  // üîπ Ore disponibile (din 35 √Æn 35 minute) cu pauzƒÉ 13‚Äì14 exclusƒÉ
  const generateTimes = () => {
    const times = [];
    let start = 8 * 60;
    const end = 20 * 60;
    while (start <= end) {
      const h = Math.floor(start / 60).toString().padStart(2, "0");
      const m = (start % 60).toString().padStart(2, "0");
      const t = `${h}:${m}`;
      if (!inBreak(t)) times.push(t); // üÜï exclude pauza
      start += SLOT_MIN; // üÜï 35 minute
    }
    return times;
  };
  const allTimes = generateTimes();

  // üîπ Citim userul logat
  useEffect(() => {
    const stored =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    setUser(stored);
  }, []);

  // üîπ Preia barberul ales din pagina /barbers
  useEffect(() => {
    const selectedBarber = JSON.parse(localStorage.getItem("selectedBarber"));
    if (selectedBarber) {
      setForm((prev) => ({ ...prev, barber_id: selectedBarber.id }));
      localStorage.removeItem("selectedBarber");
    }
  }, []);

  // üîπ √éncarcƒÉ frizerii
  useEffect(() => {
    fetch("http://localhost/barbershop/backend/api/get_barbers.php")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setBarbers(data);
      })
      .catch(() => setBarbers([]));
  }, []);

  // üîπ √éncarcƒÉ zilele libere pentru frizerul selectat
  useEffect(() => {
    if (form.barber_id) {
      fetch(
        `http://localhost/barbershop/backend/api/get_holidays.php?barber_id=${form.barber_id}`
      )
        .then((res) => res.json())
        .then((data) => {
          if (Array.isArray(data)) setHolidays(data);
        })
        .catch(() => setHolidays([]));
    }
  }, [form.barber_id]);

  // üîπ Ob»õine orele ocupate (serverul √Æntoarce sloturi individuale)
  useEffect(() => {
    if (form.date && form.barber_id) {
      fetch(
        `http://localhost/barbershop/backend/api/get_booked_times.php?date=${form.date}&barber_id=${form.barber_id}`
      )
        .then((res) => res.json())
        .then((data) => {
          const normalized = (data || []).map((t) => t.substring(0, 5));
          setBookedTimes(normalized);
        })
        .catch(() => setBookedTimes([]));
    } else {
      setBookedTimes([]);
    }
  }, [form.date, form.barber_id]);

  const isHoliday = holidays.includes(form.date);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  // üÜï reguli pentru servicii cu duratƒÉ dublƒÉ (Tuns + BarbƒÉ = 2 sloturi)
  const needsDoubleSlot = form.service === "Tuns + BarbƒÉ";

  // üÜï validare localƒÉ pentru un anumit timp (la afi»ôare)
  const isTimeSelectable = (t) => {
    const booked = bookedTimes.includes(t);
    if (booked) return false;
    if (inBreak(t)) return false; // oricum nu apare, dar pƒÉstrƒÉm regula

    if (needsDoubleSlot) {
      const next = addMinutes(t, SLOT_MIN);
      // next trebuie sƒÉ existe, sƒÉ nu fie √Æn pauzƒÉ, sƒÉ nu treacƒÉ de program »ôi sƒÉ nu fie deja ocupat
      if (!next) return false;
      if (inBreak(next)) return false;
      if (bookedTimes.includes(next)) return false;

      // dacƒÉ next nu e √Æn lista de ore (ex. 20:00 + 35), nu permitem
      if (!allTimes.includes(next)) return false;
    }
    return true;
  };

  // üîπ Trimite programarea
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    const { nume, telefon, service, barber_id, date, time } = form;

    if (!service || !barber_id || !date || !time) {
      setMessage("‚ö† CompleteazƒÉ toate c√¢mpurile!");
      return;
    }

    if (!user && (!nume || !telefon)) {
      setMessage("‚ö† CompleteazƒÉ numele »ôi telefonul!");
      return;
    }

    if (isHoliday) {
      setMessage("Frizerul este √Æn concediu √Æn aceastƒÉ zi.");
      return;
    }

    // üÜï validare extra pentru ‚ÄûTuns + BarbƒÉ‚Äù
    if (needsDoubleSlot) {
      const next = addMinutes(time, SLOT_MIN);
      if (!next) {
        setMessage("‚ö† Nu existƒÉ slotul urmƒÉtor disponibil pentru acest serviciu.");
        return;
      }
      if (inBreak(time) || inBreak(next)) {
        setMessage("‚ö† Programarea nu poate fi peste pauza 13:00‚Äì14:00.");
        return;
      }
      if (!allTimes.includes(next)) {
        setMessage("‚ö† Slotul urmƒÉtor iese din programul zilnic.");
        return;
      }
      if (bookedTimes.includes(time) || bookedTimes.includes(next)) {
        setMessage("‚ö† Ai ales o orƒÉ fƒÉrƒÉ douƒÉ sloturi consecutive libere.");
        return;
      }
    }

    const formData = new FormData();
    formData.append("barber_id", barber_id);
    formData.append("service", service);
    formData.append("date", date);
    formData.append("time", time);

    if (user) {
      formData.append("client_id", user.id);
      formData.append("nume", `${user.prenume} ${user.nume}`);
      formData.append("telefon", user.telefon);
    } else {
      formData.append("client_id", 0);
      formData.append("nume", nume);
      formData.append("telefon", telefon);
    }

    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/book_appointment.php",
        { method: "POST", body: formData }
      );
      const data = await res.json();

      if (data.success) {
        // üÜï dacƒÉ e Tuns + BarbƒÉ, blocheazƒÉ »ôi slotul urmƒÉtor local
        const next = needsDoubleSlot ? addMinutes(time, SLOT_MIN) : null;

        setMessage("‚úÖ Programarea a fost √ÆnregistratƒÉ cu succes!");
        setForm({
          nume: "",
          telefon: "",
          service: "",
          barber_id: "",
          date: "",
          time: "",
        });
        setBookedTimes((prev) =>
          needsDoubleSlot && next
            ? Array.from(new Set([...prev, time, next]))
            : Array.from(new Set([...prev, time]))
        );
      } else {
        setMessage(data.error || "‚ö† Eroare necunoscutƒÉ!");
      }
    } catch (err) {
      setMessage("‚ö† Eroare la conexiunea cu serverul!");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/50 backdrop-blur-md border border-[#d4af37]/40 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          ProgrameazƒÉ-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          {user ? (
            <div className="text-sm text-gray-300 mb-3 text-center">
              Client:{" "}
              <span className="text-[#d4af37] font-semibold">
                {user.prenume} {user.nume}
              </span>{" "}
              ({user.telefon})
            </div>
          ) : (
            <>
              <input
                type="text"
                name="nume"
                placeholder="Numele tƒÉu complet"
                value={form.nume}
                onChange={handleChange}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition text-base"
                required
              />
              <input
                type="tel"
                name="telefon"
                placeholder="NumƒÉr de telefon"
                value={form.telefon}
                onChange={handleChange}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition text-base"
                required
              />
            </>
          )}

          <select
            name="service"
            value={form.service}
            onChange={handleChange}
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:outline-none focus:border-[#d4af37] hover:border-[#d4af37] transition text-base"
            required
          >
            <option value="">‚Äî selecteazƒÉ serviciul ‚Äî</option>
            <option value="Tuns">Tuns</option>
            <option value="Tuns + BarbƒÉ">Tuns + BarbƒÉ</option>
            <option value="BarbƒÉ">BarbƒÉ</option>
            <option value="SpƒÉlat + Coafat">SpƒÉlat + Coafat</option>
          </select>

          <div className="relative flex items-center">
            <select
              name="barber_id"
              value={form.barber_id}
              onChange={handleChange}
              className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:border-[#d4af37] transition text-base appearance-none"
              required
            >
              <option value="">‚Äî selecteazƒÉ frizerul ‚Äî</option>
              {barbers.map((b) => (
                <option key={b.id} value={b.id}>
                  ‚úÇÔ∏è {b.nume} ‚Äî {b.specializare}
                </option>
              ))}
            </select>
            <Scissors className="absolute right-3 text-[#d4af37] w-5 h-5 pointer-events-none" />
          </div>

          <div className="flex gap-3 items-center justify-between">
            <div className="relative flex-1 flex items-center group">
              <input
                ref={dateRef}
                type="date"
                name="date"
                value={form.date}
                onChange={handleChange}
                min={new Date().toISOString().split("T")[0]}
                disabled={isHoliday}
                className={`w-full bg-gradient-to-br from-[#111] to-[#1f1f1f] border rounded-md px-4 py-2 pr-10 text-[#d4af37] focus:outline-none focus:ring-2 focus:ring-[#d4af37] transition text-base ${
                  isHoliday
                    ? "border-red-500 text-red-400 cursor-not-allowed"
                    : "border-[#d4af37]/50"
                }`}
                required
              />
              <button
                type="button"
                onClick={() => dateRef.current.showPicker()}
                disabled={isHoliday}
                className={`absolute right-3 ${
                  isHoliday ? "text-red-500" : "text-[#d4af37]"
                } hover:text-yellow-400 transition`}
              >
                <CalendarDays className="w-5 h-5" />
              </button>
            </div>

            <div className="relative flex-1 flex items-center">
              <select
                name="time"
                value={form.time}
                onChange={handleChange}
                disabled={isHoliday}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 pr-10 text-gray-200 focus:border-[#d4af37] transition text-base appearance-none disabled:text-gray-500 disabled:cursor-not-allowed"
                required
              >
                <option value="">HH:MM</option>
                {allTimes.map((t) => {
                  const disabled = !isTimeSelectable(t); // üÜï
                  return (
                    <option
                      key={t}
                      value={t}
                      disabled={disabled}
                      className={
                        disabled
                          ? "text-gray-500 bg-[#111]/70 cursor-not-allowed"
                          : "text-[#d4af37]"
                      }
                    >
                      {t}
                      {bookedTimes.includes(t)
                        ? " (ocupat)"
                        : needsDoubleSlot && !disabled
                        ? ""
                        : needsDoubleSlot && disabled
                        ? " (nu sunt 2 sloturi libere)"
                        : ""}
                    </option>
                  );
                })}
              </select>
              <Clock className="absolute right-3 text-[#d4af37] w-5 h-5 pointer-events-none" />
            </div>
          </div>

          {isHoliday && (
            <p className="text-red-400 text-sm text-center mt-2">
              Frizerul este √Æn concediu √Æn aceastƒÉ zi. Alege altƒÉ datƒÉ.
            </p>
          )}

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            disabled={isHoliday}
            className={`w-full mt-4 font-semibold py-3 rounded-md border transition ${
              isHoliday
                ? "bg-gray-700 text-gray-400 border-gray-600 cursor-not-allowed"
                : "bg-[#d4af37] text-black hover:bg-transparent hover:text-[#d4af37] border-[#d4af37]"
            }`}
          >
            {isHoliday ? "Zile libere" : "ProgrameazƒÉ-te acum"}
          </motion.button>
        </form>

        {message && (
          <p
            className={`mt-4 text-center text-sm ${
              message.includes("succes") ? "text-green-400" : "text-red-400"
            }`}
          >
            {message}
          </p>
        )}

        {/* üÜï info despre pauzƒÉ */}
        <p className="mt-3 text-center text-xs text-gray-400">
          PauzƒÉ zilnicƒÉ: 13:00‚Äì14:00 (nu se fac programƒÉri).
        </p>
      </motion.div>
    </section>
  );
}
