import { NavLink, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";

export default function Navbar() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("loggedUser"));
    setUser(storedUser);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("loggedUser");
    setUser(null);
    alert("Te-ai delogat cu succes!");
    navigate("/login");
  };

  return (
    <motion.nav
      initial={{ y: -40, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="backdrop-blur-md bg-black/70 border-b border-[#d4af37]/30 sticky top-0 z-50"
    >
      <div className="max-w-7xl mx-auto px-8 py-4 flex justify-between items-center">
        {/* Logo */}
        <h1 className="text-2xl font-semibold text-[#d4af37] tracking-wide">
          Denis <span className="text-white">Barbershop</span>
        </h1>

        {/* Links */}
        <div className="space-x-6 text-sm font-medium hidden md:block">
          <NavLink to="/" className={({ isActive }) => (isActive ? "text-[#d4af37]" : "text-gray-300 hover:text-[#d4af37] transition")}>
            Acasă
          </NavLink>
          <NavLink to="/services" className={({ isActive }) => (isActive ? "text-[#d4af37]" : "text-gray-300 hover:text-[#d4af37] transition")}>
            Servicii
          </NavLink>
          <NavLink to="/booking" className={({ isActive }) => (isActive ? "text-[#d4af37]" : "text-gray-300 hover:text-[#d4af37] transition")}>
            Programări
          </NavLink>
          <NavLink to="/about" className={({ isActive }) => (isActive ? "text-[#d4af37]" : "text-gray-300 hover:text-[#d4af37] transition")}>
            Despre noi
          </NavLink>
          <NavLink to="/contact" className={({ isActive }) => (isActive ? "text-[#d4af37]" : "text-gray-300 hover:text-[#d4af37] transition")}>
            Contact
          </NavLink>
          <NavLink to="/dashboard" className={({ isActive }) => (isActive ? "text-[#d4af37]" : "text-gray-300 hover:text-[#d4af37] transition")}>
            Dashboard
          </NavLink>
        </div>

        {/* Dreapta: Login/Register sau Salut + Logout */}
        <div className="flex items-center gap-4">
          {user ? (
            <>
              <span className="text-[#d4af37] font-medium">
                Salut, {user.prenume}
              </span>
              <button
                onClick={handleLogout}
                className="px-4 py-2 rounded-md border border-[#d4af37] text-[#d4af37] hover:bg-[#d4af37] hover:text-black transition"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <NavLink to="/login" className="text-gray-300 hover:text-[#d4af37] transition">
                Login
              </NavLink>
              <NavLink
                to="/register"
                className="px-4 py-2 rounded-md border border-[#d4af37] text-[#d4af37] hover:bg-[#d4af37] hover:text-black transition"
              >
                Register
              </NavLink>
            </>
          )}
        </div>
      </div>
    </motion.nav>
  );
}
