import { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { CalendarDays, Clock } from "lucide-react";

export default function Booking() {
  const [form, setForm] = useState({
    service: "",
    date: "",
    time: "",
  });
  const [user, setUser] = useState(null);
  const [bookedTimes, setBookedTimes] = useState([]); // ore ocupate din DB
  const [message, setMessage] = useState("");
  const dateRef = useRef(null);

  // ðŸ”¹ Ore Ã®ntre 08:00 È™i 20:00 din 40 Ã®n 40 minute
  const generateTimes = () => {
    const times = [];
    let start = 8 * 60;
    const end = 20 * 60;
    while (start <= end) {
      const h = Math.floor(start / 60).toString().padStart(2, "0");
      const m = (start % 60).toString().padStart(2, "0");
      times.push(`${h}:${m}`);
      start += 40;
    }
    return times;
  };
  const allTimes = generateTimes();

  // ðŸ”¹ citim userul logat
  useEffect(() => {
    const stored =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    setUser(stored);
  }, []);

  // ðŸ”¹ cerem orele ocupate pentru data selectatÄƒ
  useEffect(() => {
    if (form.date) {
      fetch(
        `http://localhost/barbershop/frontend/backend/api/get_booked_times.php?date=${form.date}`
      )
        .then((res) => res.json())
        .then((data) => {
          // normalizÄƒm datele Ã®n HH:MM
          const fixed = data.map((t) => t.slice(0, 5));
          setBookedTimes(fixed);
        })
        .catch(() => setBookedTimes([]));
    } else {
      setBookedTimes([]);
    }
  }, [form.date]);

  // ðŸ”¹ filtrÄƒm orele disponibile
  const availableTimes = allTimes.filter((t) => !bookedTimes.includes(t));

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!user) {
      setMessage("Trebuie sÄƒ fii logat pentru a face o programare!");
      return;
    }

    const { service, date, time } = form;
    if (!service || !date || !time) {
      setMessage("CompleteazÄƒ toate cÃ¢mpurile!");
      return;
    }

    const formData = new FormData();
    formData.append("client_id", user.id);
    formData.append("nume", `${user.prenume} ${user.nume}`);
    formData.append("telefon", user.telefon);
    formData.append("service", service);
    formData.append("date", date);
    formData.append("time", time);

    try {
      const res = await fetch(
        "",
        { method: "POST", body: formData }
      );
      const data = await res.json();

      if (data.success) {
        setMessage("âœ… Programarea a fost Ã®nregistratÄƒ cu succes!");
        setForm({ service: "", date: "", time: "" });
        // actualizÄƒm orele ocupate instant
        setBookedTimes((prev) => [...prev, time]);
      } else {
        setMessage(data.error);
      }
    } catch (err) {
      setMessage("Eroare la conexiunea cu serverul.");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/50 backdrop-blur-md border border-[#d4af37]/40 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          ProgrameazÄƒ-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          {user && (
            <div className="text-sm text-gray-300 mb-3">
              Client:{" "}
              <span className="text-[#d4af37] font-semibold">
                {user.prenume} {user.nume}
              </span>{" "}
              ({user.telefon})
            </div>
          )}

          <select
            name="service"
            value={form.service}
            onChange={handleChange}
            className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 text-gray-200 focus:outline-none focus:border-[#d4af37] hover:border-[#d4af37] transition text-base"
            required
          >
            <option value="">â€” selecteazÄƒ serviciul â€”</option>
            <option value="Tuns">Tuns</option>
            <option value="Tuns + BarbÄƒ">Tuns + BarbÄƒ</option>
            <option value="BarbÄƒ">BarbÄƒ</option>
            <option value="SpÄƒlat + Coafat">SpÄƒlat + Coafat</option>
          </select>

          <div className="flex gap-3 items-center justify-between">
            <div className="relative flex-1 flex items-center">
              <input
                ref={dateRef}
                type="date"
                name="date"
                value={form.date}
                onChange={handleChange}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 pr-10 text-gray-200 focus:border-[#d4af37] transition text-base"
                required
              />
              <button
                type="button"
                onClick={() => dateRef.current.showPicker()}
                className="absolute right-3 text-[#d4af37] hover:text-yellow-400 transition"
              >
                <CalendarDays className="w-5 h-5" />
              </button>
            </div>

            <div className="relative flex-1 flex items-center">
              <select
                name="time"
                value={form.time}
                onChange={handleChange}
                className="w-full bg-[#1a1a1a] border border-[#d4af37]/40 rounded-md px-4 py-2 pr-10 text-gray-200 focus:border-[#d4af37] transition text-base appearance-none"
                required
              >
                <option value="">HH:MM</option>
                {availableTimes.length > 0 ? (
                  availableTimes.map((t) => (
                    <option key={t} value={t} className="text-[#d4af37]">
                      {t}
                    </option>
                  ))
                ) : (
                  <option disabled>â€” toate orele ocupate â€”</option>
                )}
              </select>
              <Clock className="absolute right-3 text-[#d4af37] w-5 h-5 pointer-events-none" />
            </div>
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            ProgrameazÄƒ-te acum
          </motion.button>
        </form>

        {message && (
          <p
            className={`mt-4 text-center text-sm ${
              message.includes("succes")
                ? "text-green-400"
                : "text-red-400"
            }`}
          >
            {message}
          </p>
        )}
      </motion.div>
    </section>
  );
}
