import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

export default function Booking() {
  const navigate = useNavigate();
  const loggedUser = JSON.parse(localStorage.getItem("loggedUser"));

  const [form, setForm] = useState({
    nume: loggedUser?.nume || "",
    prenume: loggedUser?.prenume || "",
    telefon: loggedUser?.telefon || "",
    serviciu: "",
    data: "",
    ora: "",
  });

  const [oreDisponibile, setOreDisponibile] = useState([]);

  // ore standard (de exemplu, 9:00–18:00 din oră în oră)
  const oreLucru = [
    "09:00", "10:00", "11:00", "12:00",
    "13:00", "14:00", "15:00", "16:00",
    "17:00", "18:00"
  ];

  // actualizăm orele disponibile când se schimbă data
  useEffect(() => {
    if (!form.data) {
      setOreDisponibile([]);
      return;
    }

    const bookings = JSON.parse(localStorage.getItem("bookings") || "[]");

    // găsim orele deja rezervate în acea zi
    const ocupate = bookings
      .filter(b => b.data === form.data)
      .map(b => b.ora);

    // setăm orele libere
    const libere = oreLucru.filter(ora => !ocupate.includes(ora));
    setOreDisponibile(libere);
  }, [form.data]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!form.nume || !form.prenume || !form.telefon || !form.serviciu || !form.data || !form.ora) {
      alert("Completează toate câmpurile!");
      return;
    }

    const bookings = JSON.parse(localStorage.getItem("bookings") || "[]");

    // prevenim dublurile
    const duplicate = bookings.find(
      b => b.data === form.data && b.ora === form.ora
    );
    if (duplicate) {
      alert("Acea oră este deja rezervată! Alege alta.");
      return;
    }

    const updatedBookings = [...bookings, form];
    localStorage.setItem("bookings", JSON.stringify(updatedBookings));

    alert("Programare adăugată cu succes!");
    navigate("/dashboard");
  };

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white flex items-center justify-center">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="bg-black/40 backdrop-blur-md border border-[#d4af37]/30 rounded-2xl p-10 w-full max-w-lg shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-8">
          Programează-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          <div>
            <label className="block text-sm text-gray-300 mb-2">Nume (Familie)</label>
            <input
              type="text"
              name="nume"
              value={form.nume}
              onChange={handleChange}
              className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 focus:border-[#d4af37] outline-none"
            />
          </div>

          <div>
            <label className="block text-sm text-gray-300 mb-2">Prenume (Nume mic)</label>
            <input
              type="text"
              name="prenume"
              value={form.prenume}
              onChange={handleChange}
              className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 focus:border-[#d4af37] outline-none"
            />
          </div>

          <div>
            <label className="block text-sm text-gray-300 mb-2">Număr de telefon</label>
            <input
              type="text"
              name="telefon"
              value={form.telefon}
              onChange={handleChange}
              className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 focus:border-[#d4af37] outline-none"
              disabled={!!loggedUser}
            />
          </div>

          <div>
            <label className="block text-sm text-gray-300 mb-2">Serviciu</label>
            <select
              name="serviciu"
              value={form.serviciu}
              onChange={handleChange}
              className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 focus:border-[#d4af37] outline-none"
            >
              <option value="">— selectează serviciul —</option>
              <option value="Tuns">Tuns</option>
              <option value="Tuns + Barba">Tuns + Barba</option>
              <option value="Barba">Barba</option>
              <option value="Spălat + Styling">Spălat + Styling</option>
            </select>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm text-gray-300 mb-2">Data</label>
              <input
                type="date"
                name="data"
                value={form.data}
                onChange={handleChange}
                min={new Date().toISOString().split("T")[0]} // nu poți alege date trecute
                className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 focus:border-[#d4af37] outline-none"
              />
            </div>

            <div>
              <label className="block text-sm text-gray-300 mb-2">Ora</label>
              <select
                name="ora"
                value={form.ora}
                onChange={handleChange}
                className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 focus:border-[#d4af37] outline-none"
              >
                <option value="">— selectează ora —</option>
                {oreDisponibile.map((ora) => (
                  <option key={ora} value={ora}>
                    {ora}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-6 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition duration-300"
          >
            Programează-te acum
          </motion.button>
        </form>
      </motion.div>
    </section>
  );
}
