import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [search, setSearch] = useState("");
  const [holidays, setHolidays] = useState([]); // üå¥ adƒÉugat pentru concedii
  const [selectedHoliday, setSelectedHoliday] = useState("");

  // üß© PreluƒÉm userul logat
  useEffect(() => {
    const stored =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    setUser(stored);
    if (stored) {
      fetchAppointments(stored.id);
      fetchHolidays(stored.id);
    }
  }, []);

  // üîÑ √éncarcƒÉ programƒÉrile
const fetchAppointments = async () => {
  try {
    const user = JSON.parse(localStorage.getItem("loggedUser")) || JSON.parse(sessionStorage.getItem("loggedUser"));
    if (!user || !user.id) {
      setAppointments([]);
      return;
    }

    const res = await fetch(
      `http://localhost/barbershop/frontend/backend/api/get_all_appointments.php?barber_id=${user.barber_id || user.id}`
    );
    const data = await res.json();

    if (Array.isArray(data)) {
      data.sort(
        (a, b) =>
          new Date(`${a.date}T${a.time}`) - new Date(`${b.date}T${b.time}`)
      );
      setAppointments(data);
    } else {
      setAppointments([]);
    }
  } catch {
    setAppointments([]);
  }
};


  // üîÅ √éncarcƒÉ zilele libere din DB
  const fetchHolidays = async (barberId) => {
    try {
      const res = await fetch(
        `http://localhost/barbershop/frontend/backend/api/get_holidays.php?barber_id=${barberId}`
      );
      const data = await res.json();
      if (data.success) setHolidays(data.holidays);
    } catch (err) {
      console.error("Eroare holidays:", err);
    }
  };

  // üß© √éncepem editarea
  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  // üíæ SalvƒÉm modificƒÉrile
  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();

      if (data.success) {
        setMessage("‚úÖ Programarea a fost actualizatƒÉ!");
        setEditingId(null);
        if (user) fetchAppointments(user.id);
      } else setMessage("‚ö† Eroare la actualizare!");
    } catch (err) {
      console.error(err);
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üóëÔ∏è »òtergere programare
  const handleDelete = async (id) => {
    if (!window.confirm("Sigur vrei sƒÉ »ôtergi aceastƒÉ programare?")) return;

    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/delete_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id }),
        }
      );

      const data = await res.json();
      if (data.success) {
        setMessage("üóëÔ∏è Programarea a fost »ôtearsƒÉ!");
        setAppointments((prev) => prev.filter((a) => a.id !== id));
      } else setMessage("‚ö† Eroare la »ôtergere!");
    } catch (err) {
      console.error("Eroare conexiune:", err);
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üå¥ AdaugƒÉ zi liberƒÉ
  const handleAddHoliday = async () => {
    if (!selectedHoliday) {
      setMessage("‚ö† SelecteazƒÉ o datƒÉ pentru concediu!");
      return;
    }
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/set_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date: selectedHoliday }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üå¥ Ziua liberƒÉ a fost adƒÉugatƒÉ!");
        fetchHolidays(user.id);
      } else {
        setMessage(data.error || "‚ö† Eroare la salvare!");
      }
    } catch (err) {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üóëÔ∏è »òterge o zi liberƒÉ
  const handleDeleteHoliday = async (date) => {
    if (!window.confirm("»òtergi aceastƒÉ zi liberƒÉ?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/frontend/backend/api/delete_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("üóìÔ∏è Ziua liberƒÉ a fost eliminatƒÉ!");
        fetchHolidays(user.id);
      } else {
        setMessage("‚ö† Eroare la »ôtergere!");
      }
    } catch (err) {
      setMessage("‚ö† Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  // üîç Filtrare avansatƒÉ
  const filteredAppointments = appointments.filter((a) => {
    const term = search.toLowerCase().trim();
    const normalizePhone = (num) =>
      num ? num.replace("+373", "0").replace(/\s+/g, "").trim() : "";

    return (
      a.client_nume.toLowerCase().includes(term) ||
      a.client_prenume.toLowerCase().includes(term) ||
      normalizePhone(a.client_telefon).includes(term.replace("+373", "0"))
    );
  });

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white p-8 overflow-auto">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-6xl mx-auto bg-black/40 border border-[#d4af37]/40 rounded-2xl p-8 shadow-lg"
      >
        <h1 className="text-3xl font-bold text-[#d4af37] mb-4 text-center">
          Dashboard ‚Äî {user?.prenume || "Barber"}
        </h1>

        {/* üå¥ Zile libere / Concediu */}
        <div className="mb-8 bg-[#1a1a1a]/50 border border-[#d4af37]/30 rounded-xl p-5">
          <h2 className="text-xl font-semibold text-[#d4af37] mb-3 text-center">
            Zile libere / Concediu
          </h2>

          <div className="flex flex-col sm:flex-row items-center justify-center gap-3">
            <input
              type="date"
              value={selectedHoliday}
              onChange={(e) => setSelectedHoliday(e.target.value)}
              min={new Date().toISOString().split("T")[0]}
              className="bg-[#111] border border-[#d4af37]/50 rounded-md px-3 py-2 text-[#d4af37] focus:border-[#d4af37] focus:outline-none"
            />
            <button
              onClick={handleAddHoliday}
              className="bg-[#d4af37] text-black font-semibold px-5 py-2 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
            >
              SalveazƒÉ concediul
            </button>
          </div>

          {holidays.length > 0 && (
            <ul className="mt-4 text-center text-sm text-gray-300">
              {holidays.map((h, i) => (
                <li key={i} className="mb-2">
                  {h.date}{" "}
                  <button
                    onClick={() => handleDeleteHoliday(h.date)}
                    className="text-red-500 hover:underline ml-2"
                  >
                    »òterge
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* üîç Bara de cƒÉutare */}
        <div className="flex justify-center mb-6">
          <input
            type="text"
            placeholder="CautƒÉ client dupƒÉ nume, prenume sau telefon..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full max-w-md bg-[#1a1a1a] border border-[#d4af37]/40 text-[#d4af37] placeholder-gray-500 rounded-md px-4 py-2 focus:border-[#d4af37] focus:outline-none"
          />
        </div>

        {message && (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className={`text-center mb-4 font-medium ${
              message.includes("‚úÖ") ||
              message.includes("üóëÔ∏è") ||
              message.includes("üå¥")
                ? "text-green-400"
                : "text-red-400"
            }`}
          >
            {message}
          </motion.p>
        )}

        {/* üìÖ Tabel programƒÉri */}
        {filteredAppointments.length === 0 ? (
          <p className="text-center text-gray-400">
            Niciun rezultat gƒÉsit pentru termenul introdus.
          </p>
        ) : (
          <div className="overflow-x-auto max-h-[70vh]">
            <table className="w-full text-sm border-collapse">
              <thead className="bg-[#1a1a1a] text-[#d4af37] sticky top-0">
                <tr>
                  <th className="p-3 border border-[#d4af37]/30">#</th>
                  <th className="p-3 border border-[#d4af37]/30">Client</th>
                  <th className="p-3 border border-[#d4af37]/30">Telefon</th>
                  <th className="p-3 border border-[#d4af37]/30">Serviciu</th>
                  <th className="p-3 border border-[#d4af37]/30">Data</th>
                  <th className="p-3 border border-[#d4af37]/30">Ora</th>
                  <th className="p-3 border border-[#d4af37]/30">Ac»õiuni</th>
                </tr>
              </thead>
              <tbody>
                {filteredAppointments.map((a, i) => (
                  <tr
                    key={a.id}
                    className="hover:bg-[#d4af37]/10 transition text-center"
                  >
                    <td className="p-2 border border-[#d4af37]/20">{i + 1}</td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.client_prenume} {a.client_nume}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.client_telefon}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.service}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="date"
                          min={new Date().toISOString().split("T")[0]}
                          value={edited.date}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              date: e.target.value,
                            }))
                          }
                          className="bg-[#111] border border-[#d4af37]/50 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        <span className="text-gray-200">{a.date}</span>
                      )}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="time"
                          value={edited.time}
                          onChange={(e) =>
                            setEdited((prev) => ({
                              ...prev,
                              time: e.target.value,
                            }))
                          }
                          className="bg-[#111] border border-[#d4af37]/50 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        <span className="text-gray-200">{a.time}</span>
                      )}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20 flex justify-center gap-2">
                      {editingId === a.id ? (
                        <button
                          onClick={() => handleSave(a.id)}
                          className="bg-[#d4af37] text-black px-3 py-1 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37]"
                        >
                          SalveazƒÉ
                        </button>
                      ) : (
                        <>
                          <button
                            onClick={() => handleEdit(a)}
                            className="text-[#d4af37] hover:underline"
                          >
                            EditeazƒÉ
                          </button>
                          <button
                            onClick={() => handleDelete(a.id)}
                            className="text-red-500 hover:underline"
                          >
                            »òterge
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </motion.div>
    </section>
  );
}
