import { useState } from "react";
import { motion } from "framer-motion";

export default function Booking() {
  const [form, setForm] = useState({
    nume: "",
    prenume: "",
    telefon: "",
    service: "",
    date: "",
    time: "",
  });

  const user = JSON.parse(localStorage.getItem("loggedUser"));

  // completăm automat datele din profil dacă există
  if (user && !form.nume && !form.prenume && !form.telefon) {
    setForm({
      ...form,
      nume: user.nume,
      prenume: user.prenume,
      telefon: user.telefon,
    });
  }

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    const bookings = JSON.parse(localStorage.getItem("bookings") || "[]");
    bookings.push(form);
    localStorage.setItem("bookings", JSON.stringify(bookings));
    alert("Programarea a fost înregistrată cu succes!");
    setForm({
      nume: "",
      prenume: "",
      telefon: "",
      service: "",
      date: "",
      time: "",
    });
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/40 backdrop-blur-md border border-[#d4af37]/30 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          Programează-te la tuns
        </h2>

        <form onSubmit={handleSubmit} className="space-y-6">
          <input
            type="text"
            name="nume"
            value={form.nume}
            onChange={handleChange}
            placeholder="Nume (familie)"
            className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2"
            required
          />

          <input
            type="text"
            name="prenume"
            value={form.prenume}
            onChange={handleChange}
            placeholder="Prenume (nume mic)"
            className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2"
            required
          />

          <input
            type="tel"
            name="telefon"
            value={form.telefon}
            onChange={handleChange}
            placeholder="Număr de telefon"
            className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2"
            required
          />

          <select
            name="service"
            value={form.service}
            onChange={handleChange}
            className="w-full bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 text-gray-300"
            required
          >
            <option value="">— selectează serviciul —</option>
            <option value="Tuns">Tuns</option>
            <option value="Tuns + Barbă">Tuns + Barbă</option>
            <option value="Barbă">Barbă</option>
            <option value="Spălat + Coafat">Spălat + Coafat</option>
          </select>

          <div className="flex gap-3">
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleChange}
              className="flex-1 bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 text-gray-300"
              required
            />
            <input
              type="time"
              name="time"
              value={form.time}
              onChange={handleChange}
              className="flex-1 bg-transparent border border-[#d4af37]/30 rounded-md px-4 py-2 text-gray-300"
              required
            />
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
          >
            Programează-te acum
          </motion.button>
        </form>
      </motion.div>
    </section>
  );
}
