import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [telefon, setTelefon] = useState("");
  const [adminCode, setAdminCode] = useState("");
  const [error, setError] = useState("");
  const [remember, setRemember] = useState(false);
  const [isAdminAttempt, setIsAdminAttempt] = useState(false);
  const navigate = useNavigate();

  // ðŸ”¸ DacÄƒ utilizatorul e deja logat, Ã®l redirecÈ›ionÄƒm
  useEffect(() => {
    const savedUser =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    if (savedUser) {
      window.dispatchEvent(new Event("userUpdated"));
      navigate(savedUser.isAdmin ? "/dashboard" : "/");
    }
  }, [navigate]);

  // ðŸ” DacÄƒ telefonul corespunde unui admin, cerem codul
  useEffect(() => {
    if (telefon.startsWith("+3736") || telefon.startsWith("0767")) {
      setIsAdminAttempt(true);
    } else {
      setIsAdminAttempt(false);
      setAdminCode("");
    }
  }, [telefon]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    if (!telefon.trim()) {
      setError("Introdu numÄƒrul de telefon!");
      return;
    }

    const formData = new FormData();
    formData.append("telefon", telefon);
    if (isAdminAttempt) formData.append("cod_acces", adminCode);

    try {
      // ðŸ”¹ 1. ÃŽncearcÄƒ autentificarea ca ADMIN
      const adminRes = await fetch(
        "http://localhost/barbershop/frontend/backend/api/login_admin.php",
        {
          method: "POST",
          body: formData,
        }
      );
      const adminData = await adminRes.json();

      if (adminData.success) {
        const adminUser = {
          ...adminData.admin,
          isAdmin: true,
        };
        if (remember)
          localStorage.setItem("loggedUser", JSON.stringify(adminUser));
        else sessionStorage.setItem("loggedUser", JSON.stringify(adminUser));

        window.dispatchEvent(new Event("userUpdated"));
        alert(`ðŸ‘‘ Bun venit, ${adminUser.prenume}!`);
        navigate("/dashboard");
        return;
      }

      // ðŸ”¹ 2. DacÄƒ nu e admin â†’ verificÄƒm Ã®n tabela `clients`
      const clientRes = await fetch(
        "http://localhost/barbershop/frontend/backend/api/login_client.php",
        {
          method: "POST",
          body: formData,
        }
      );
      const clientData = await clientRes.json();

      if (clientData.success) {
        const user = clientData.user;
        if (remember)
          localStorage.setItem("loggedUser", JSON.stringify(user));
        else sessionStorage.setItem("loggedUser", JSON.stringify(user));

        window.dispatchEvent(new Event("userUpdated"));
        alert(`Bun venit, ${user.prenume} ${user.nume}!`);
        navigate("/");
      } else {
        setError(clientData.error || "NumÄƒr de telefon inexistent!");
      }
    } catch (err) {
      console.error(err);
      setError("Eroare la conexiunea cu serverul.");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/40 backdrop-blur-md border border-[#d4af37]/30 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          Autentificare
        </h2>

        <form onSubmit={handleLogin} className="space-y-6">
          {/* Telefon */}
          <div>
            <label className="block text-sm text-gray-300 mb-2">
              NumÄƒr de telefon
            </label>
            <input
              type="tel"
              value={telefon}
              onChange={(e) => {
                setTelefon(e.target.value);
                setError("");
              }}
              placeholder="+37360000000 sau 076784211"
              className={`w-full bg-transparent border ${
                error
                  ? "border-red-500 focus:border-red-500"
                  : "border-[#d4af37]/30 focus:border-[#d4af37]"
              } text-white rounded-md px-4 py-2 focus:outline-none transition`}
            />
          </div>

          {/* ðŸ”‘ Cod de acces pentru admin */}
          {isAdminAttempt && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <label className="block text-sm text-gray-300 mb-2">
                Cod de acces admin
              </label>
              <input
                type="password"
                value={adminCode}
                onChange={(e) => setAdminCode(e.target.value)}
                placeholder="Introdu codul secret"
                className={`w-full bg-transparent border ${
                  error
                    ? "border-red-500 focus:border-red-500"
                    : "border-[#d4af37]/30 focus:border-[#d4af37]"
                } text-white rounded-md px-4 py-2 focus:outline-none transition`}
              />
            </motion.div>
          )}

          {error && <p className="text-red-500 text-xs">{error}</p>}

          {/* Remember me */}
          <div className="flex items-center justify-between text-sm mt-3">
            <label className="flex items-center gap-2 cursor-pointer select-none">
              <input
                type="checkbox"
                checked={remember}
                onChange={() => setRemember(!remember)}
                className="accent-[#d4af37] w-4 h-4"
              />
              <span className="text-gray-300">Èšine-mÄƒ minte</span>
            </label>

            <a
              href="/register"
              className="text-[#d4af37] hover:underline transition"
            >
              ÃŽnregistreazÄƒ-te
            </a>
          </div>

          {/* Buton Login */}
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-5 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition duration-300"
          >
            AutentificÄƒ-te
          </motion.button>
        </form>

        <p className="text-center text-gray-400 text-xs mt-8">
          Â© 2025 Denis Barbershop. Toate drepturile rezervate.
        </p>
      </motion.div>
    </section>
  );
}
