import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [telefon, setTelefon] = useState("");
  const [adminCode, setAdminCode] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // ðŸ”¸ Adminii autorizaÈ›i
  const admins = [
    { id: 1, phone: "+37360000000", prenume: "Denis" },
    { id: 2, phone: "076784211", prenume: "Danu" },
  ];

  // ðŸ”‘ Codul secret unic pentru acces
  const adminAccessCode = "7UVJ5tNnZYni8fQuazbvElFbZcXx9aTTkBaF1v";

  // âœ… DacÄƒ e deja logat, trimite-l Ã®n Dashboard
  useEffect(() => {
    const savedUser =
      JSON.parse(localStorage.getItem("loggedUser")) ||
      JSON.parse(sessionStorage.getItem("loggedUser"));
    if (savedUser?.isAdmin) navigate("/dashboard");
  }, [navigate]);

  const handleLogin = (e) => {
    e.preventDefault();
    setError("");

    if (!telefon.trim()) {
      setError("Introdu numÄƒrul de telefon!");
      return;
    }

    const normalized = telefon.replace(/\s|-/g, "");
    const adminFound = admins.find(
      (a) =>
        normalized.endsWith(a.phone) ||
        a.phone.endsWith(normalized) ||
        normalized === a.phone
    );

    if (!adminFound) {
      setError("Acces interzis! Doar adminii pot intra.");
      return;
    }

    if (adminCode !== adminAccessCode) {
      setError("Codul secret este greÈ™it!");
      return;
    }

    // âœ… CreÄƒm userul local
    const adminUser = {
      id: adminFound.id,
      prenume: adminFound.prenume,
      telefon: adminFound.phone,
      isAdmin: true,
    };

    localStorage.setItem("loggedUser", JSON.stringify(adminUser));
    window.dispatchEvent(new Event("userUpdated"));

    alert(`âœ… Bun venit, ${adminFound.prenume} (Admin)!`);
    navigate("/dashboard");
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/40 backdrop-blur-md border border-[#d4af37]/30 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          Autentificare Admin
        </h2>

        <form onSubmit={handleLogin} className="space-y-6">
          {/* Telefon */}
          <div>
            <label className="block text-sm text-gray-300 mb-2">
              NumÄƒr de telefon
            </label>
            <input
              type="tel"
              value={telefon}
              onChange={(e) => setTelefon(e.target.value)}
              placeholder="+37360000000"
              className={`w-full bg-transparent border ${
                error
                  ? "border-red-500 focus:border-red-500"
                  : "border-[#d4af37]/30 focus:border-[#d4af37]"
              } text-white rounded-md px-4 py-2 focus:outline-none transition`}
            />
          </div>

          {/* Cod acces admin */}
          <div>
            <label className="block text-sm text-gray-300 mb-2">
              Cod de acces
            </label>
            <input
              type="password"
              value={adminCode}
              onChange={(e) => setAdminCode(e.target.value)}
              placeholder="Introdu codul secret"
              className={`w-full bg-transparent border ${
                error
                  ? "border-red-500 focus:border-red-500"
                  : "border-[#d4af37]/30 focus:border-[#d4af37]"
              } text-white rounded-md px-4 py-2 focus:outline-none transition`}
            />
          </div>

          {error && <p className="text-red-500 text-xs">{error}</p>}

          {/* Buton Login */}
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-5 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition duration-300"
          >
            AutentificÄƒ-te
          </motion.button>
        </form>

        <p className="text-center text-gray-400 text-xs mt-8">
          Accesul este permis doar administratorilor Ã®nregistraÈ›i.
        </p>
      </motion.div>
    </section>
  );
}
