import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [bookings, setBookings] = useState([]);

  // ðŸ”„ La Ã®ncÄƒrcare: verificÄƒm programÄƒrile È™i curÄƒÈ›Äƒm cele expirate
  useEffect(() => {
    const storedBookings = JSON.parse(localStorage.getItem("bookings") || "[]");
    const now = new Date();

    // FiltrÄƒm doar programÄƒrile valide
    const validBookings = storedBookings.filter((booking) => {
      if (!booking.date || !booking.time) return false;

      const bookingTime = new Date(`${booking.date}T${booking.time}`);
      const diffMinutes = (now - bookingTime) / 1000 / 60;

      // dacÄƒ programarea e mai veche de 15 minute â†’ o eliminÄƒm
      return diffMinutes < 15;
    });

    // actualizÄƒm dacÄƒ s-au È™ters unele
    if (validBookings.length !== storedBookings.length) {
      localStorage.setItem("bookings", JSON.stringify(validBookings));
    }

    setBookings(validBookings);
  }, []);

  const handleDelete = (index) => {
    const updated = [...bookings];
    updated.splice(index, 1);
    setBookings(updated);
    localStorage.setItem("bookings", JSON.stringify(updated));
  };

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white py-12">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-5xl mx-auto px-6"
      >
        <h1 className="text-3xl md:text-4xl font-bold text-center text-[#d4af37] mb-10">
          ProgramÄƒri â€” Panou Admin
        </h1>

        {bookings.length === 0 ? (
          <p className="text-center text-gray-400">Nu existÄƒ programÄƒri active.</p>
        ) : (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {bookings.map((b, i) => (
              <div
                key={i}
                className="bg-black/50 border border-[#d4af37]/30 rounded-lg p-6 shadow-lg relative"
              >
                <p><strong>Client:</strong> {b.prenume} {b.nume}</p>
                <p><strong>Telefon:</strong> {b.telefon}</p>
                <p><strong>Serviciu:</strong> {b.service}</p>
                <p><strong>Data:</strong> {b.date || "NespecificatÄƒ"}</p>
                <p><strong>Ora:</strong> {b.time || "â€”"}</p>

                <button
                  onClick={() => handleDelete(i)}
                  className="absolute bottom-3 right-4 text-red-500 hover:underline text-sm"
                >
                  È˜terge
                </button>
              </div>
            ))}
          </div>
        )}
      </motion.div>
    </section>
  );
}
