import { NavLink } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import { useState } from "react";
import { Menu, X } from "lucide-react";

export default function Navbar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // 沐 Link-uri statice
  const navigationLinks = [
    { to: "/", label: "Acasﾄソ

/services", label: "Servicii" },
    { to: "/booking", label: "Programﾄビi" },
    { to: "/about", label: "Despre noi" },
    { to: "/contact", label: "Contact" },
  ];

  return (
    <motion.nav
      initial={{ y: -40, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="backdrop-blur-md bg-black/70 border-b border-[#d4af37]/30 sticky top-0 z-50"
    >
      <div className="max-w-7xl mx-auto px-6 md:px-8 py-4 flex justify-between items-center">
        {/* 沐ｹ Logo */}
        <h1 className="text-2xl font-semibold text-[#d4af37] tracking-wide cursor-pointer select-none">
          Denis <span className="text-white">Barbershop</span>
        </h1>

        {/* 沐ｹ Meniu Desktop */}
        <div className="hidden md:flex space-x-6 text-sm font-medium">
          {navigationLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                isActive
                  ? "text-[#d4af37]"
                  : "text-gray-300 hover:text-[#d4af37] transition"
              }
            >
              {link.label}
            </NavLink>
          ))}
        </div>

        {/* 沐ｹ Dreapta: buton simplu */}
        <div className="hidden md:flex items-center gap-4">
          <button className="px-4 py-2 rounded-md border border-[#d4af37] text-[#d4af37] hover:bg-[#d4af37] hover:text-black transition">
            Admin Login
          </button>
        </div>

        {/* 沐ｽ Meniu mobil */}
        <button
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className="md:hidden text-[#d4af37] focus:outline-none"
        >
          {isMenuOpen ? <X size={26} /> : <Menu size={26} />}
        </button>
      </div>

      {/* 沐ｹ Meniu mobil animat */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.3 }}
            className="md:hidden bg-black/90 backdrop-blur-lg border-t border-[#d4af37]/30 text-center py-4 space-y-4"
          >
            {navigationLinks.map((link) => (
              <NavLink
                key={link.to}
                to={link.to}
                onClick={() => setIsMenuOpen(false)}
                className={({ isActive }) =>
                  isActive
                    ? "block text-[#d4af37] font-semibold"
                    : "block text-gray-300 hover:text-[#d4af37] transition"
                }
              >
                {link.label}
              </NavLink>
            ))}

            {/* 沐 Buton mobil simplu */}
            <div className="pt-4 border-t border-[#d4af37]/20">
              <button
                onClick={() => setIsMenuOpen(false)}
                className="px-4 py-2 rounded-md border border-[#d4af37] text-[#d4af37] hover:bg-[#d4af37] hover:text-black transition"
              >
                Admin Login
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.nav>
  );
}
