import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [edited, setEdited] = useState({ date: "", time: "" });
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);
  const [search, setSearch] = useState("");
  const [holidays, setHolidays] = useState([]);
  const [selectedHoliday, setSelectedHoliday] = useState("");

  // --- helper fetch pentru appointments
  const fetchAppointments = async (barberId) => {
    if (!barberId) {
      console.warn("fetchAppointments apelat fÄƒrÄƒ barberId!");
      setAppointments([]);
      return;
    }

    try {
      const url = `http://localhost/barbershop/backend/api/get_barber_appointments.php?barber_id=${encodeURIComponent(
        barberId
      )}`;
      console.log("fetchAppointments ->", url);
      const res = await fetch(url, { cache: "no-store" });
      const data = await res.json();
      console.log("fetchAppointments response:", data);

      if (data && data.success && Array.isArray(data.appointments)) {
        const sorted = data.appointments.sort(
          (a, b) =>
            new Date(`${a.date}T${a.time}`) - new Date(`${b.date}T${b.time}`)
        );
        setAppointments(sorted);
      } else {
        console.warn("fetchAppointments: rÄƒspuns invalid, setAppointments([])");
        setAppointments([]);
      }
    } catch (err) {
      console.error("fetchAppointments error:", err);
      setAppointments([]);
    }
  };

  const fetchHolidays = async (barberId) => {
    if (!barberId) return setHolidays([]);
    try {
      const res = await fetch(
        `http://localhost/barbershop/backend/api/get_holidays.php?barber_id=${encodeURIComponent(
          barberId
        )}`,
        { cache: "no-store" }
      );
      const data = await res.json();
      if (data && data.success) {
        // normalizeazÄƒ fie [{date:'YYYY-MM-DD'}] fie ['YYYY-MM-DD']
        const normalized = Array.isArray(data.holidays)
          ? data.holidays
              .map((h) => (typeof h === "string" ? h : h?.date || null))
              .filter(Boolean)
          : [];
        setHolidays(normalized);
      } else {
        setHolidays([]);
      }
    } catch (err) {
      console.error("Eroare holidays:", err);
      setHolidays([]);
    }
  };

  // --- iniÈ›ial: citim loggedUser È™i apelÄƒm fetch
  useEffect(() => {
    const raw =
      localStorage.getItem("loggedUser") || sessionStorage.getItem("loggedUser");
    let stored = null;
    try {
      stored = raw ? JSON.parse(raw) : null;
    } catch (err) {
      console.error("loggedUser parse error:", err, raw);
      stored = null;
    }

    if (!stored || !stored.id) {
      console.warn("Nu existÄƒ user autentificat sau lipseÈ™te id-ul:", stored);
      setUser(null);
      setAppointments([]); // curÄƒÈ›Äƒm view-ul pentru siguranÈ›Äƒ
      return;
    }

    setUser(stored);
    console.log("Dashboard: fetchAppointments pentru barber_id =", stored.id);
    fetchAppointments(stored.id);
    fetchHolidays(stored.id);
  }, []);

  // --- edit/save/delete/holidays/cleanup (foloseÈ™te exact ce ai deja)
  const handleEdit = (appt) => {
    setEditingId(appt.id);
    setEdited({ date: appt.date, time: appt.time });
  };

  const handleSave = async (id) => {
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/update_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id, date: edited.date, time: edited.time }),
        }
      );
      const data = await res.json();

      if (data.success) {
        setMessage("âœ… Programarea a fost actualizatÄƒ!");
        setEditingId(null);
        if (user) fetchAppointments(user.id);
      } else setMessage("âš  Eroare la actualizare!");
    } catch (err) {
      console.error(err);
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Sigur vrei sÄƒ È™tergi aceastÄƒ programare?")) return;

    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_appointment.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ id }),
        }
      );

      const data = await res.json();
      if (data.success) {
        setMessage("ðŸ—‘ï¸ Programarea a fost È™tearsÄƒ!");
        setAppointments((prev) => prev.filter((a) => a.id !== id));
      } else setMessage("âš  Eroare la È™tergere!");
    } catch (err) {
      console.error("Eroare conexiune:", err);
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleAddHoliday = async () => {
    if (!selectedHoliday) {
      setMessage("âš  SelecteazÄƒ o datÄƒ pentru concediu!");
      return;
    }
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/set_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date: selectedHoliday }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("ðŸŒ´ Ziua liberÄƒ a fost adÄƒugatÄƒ!");
        fetchHolidays(user.id);
      } else {
        setMessage(data.error || "âš  Eroare la salvare!");
      }
    } catch (err) {
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleDeleteHoliday = async (date) => {
    if (!window.confirm("È˜tergi aceastÄƒ zi liberÄƒ?")) return;
    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_holiday.php",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ barber_id: user.id, date }),
        }
      );
      const data = await res.json();
      if (data.success) {
        setMessage("ðŸ—“ï¸ Ziua liberÄƒ a fost eliminatÄƒ!");
        fetchHolidays(user.id);
      } else {
        setMessage("âš  Eroare la È™tergere!");
      }
    } catch (err) {
      setMessage("âš  Eroare de conexiune!");
    } finally {
      setTimeout(() => setMessage(""), 2500);
    }
  };

  const handleCleanupOldAppointments = async () => {
    if (
      !window.confirm(
        "Sigur vrei sÄƒ È™tergi toate programÄƒrile din zilele trecute?"
      )
    )
      return;

    try {
      const res = await fetch(
        "http://localhost/barbershop/backend/api/delete_old_appointments.php"
      );
      const data = await res.json();
      if (data.success) {
        setMessage(`ðŸ§¹ ${data.message} (È˜terse: ${data.deleted_count})`);
        if (user) fetchAppointments(user.id);
      } else {
        setMessage("âš  Eroare la curÄƒÈ›are!");
      }
    } catch (err) {
      console.error(err);
      setMessage("âš  Eroare de conexiune la server!");
    } finally {
      setTimeout(() => setMessage(""), 3000);
    }
  };

  const filteredAppointments = appointments.filter((a) => {
    const normalize = (str) =>
      (str || "").toLowerCase().replace(/\s+/g, "").trim();

    const term = normalize(search);
    const clientName = normalize(a.client_nume || a.nume || "");
    const phone = normalize(a.client_telefon || a.telefon || "");
    const service = normalize(a.service || "");
    const barber = normalize(a.barber_name || a.barber_prenume || "");

    return (
      clientName.includes(term) ||
      phone.includes(term) ||
      service.includes(term) ||
      barber.includes(term)
    );
  });

  // fallback UI when not logged properly
  if (!user || !user.id) {
    return (
      <section className="bg-[#0f0f0f] text-white p-8 flex justify-center">
        <motion.div
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="w-full max-w-4xl bg-black/40 border border-[#d4af37]/40 rounded-2xl p-8 shadow-lg"
        >
          <p className="text-center text-red-400">
            Nu eÈ™ti autentificat corect. Re-autentificÄƒ-te pentru a vedea
            dashboard-ul.
          </p>
        </motion.div>
      </section>
    );
  }

  return (
    <section className="bg-[#0f0f0f] text-white p-8 flex justify-center">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="w-full max-w-6xl bg-black/40 border border-[#d4af37]/40 rounded-2xl p-8 shadow-lg overflow-y-auto h-[calc(100vh-120px)]"
      >
        <h1 className="text-3xl font-bold text-[#d4af37] mb-4 text-center">
          Dashboard â€” {user?.prenume || "Barber"}
        </h1>

        <div className="flex justify-center mb-6">
          <button
            onClick={handleCleanupOldAppointments}
            className="bg-red-600 hover:bg-red-700 text-white font-semibold px-6 py-2 rounded-md transition border border-red-400 hover:border-red-500"
          >
            ðŸ§¹ È˜terge programÄƒrile vechi
          </button>
        </div>

        <div className="mb-8 bg-[#1a1a1a]/50 border border-[#d4af37]/30 rounded-xl p-5">
          <h2 className="text-xl font-semibold text-[#d4af37] mb-3 text-center">
            Zile libere / Concediu
          </h2>

          <div className="flex flex-col sm:flex-row items-center justify-center gap-3">
            <input
              type="date"
              value={selectedHoliday}
              onChange={(e) => setSelectedHoliday(e.target.value)}
              min={new Date().toISOString().split("T")[0]}
              className="bg-[#111] border border-[#d4af37]/50 rounded-md px-3 py-2 text-[#d4af37] focus:border-[#d4af37] focus:outline-none"
            />
            <button
              onClick={handleAddHoliday}
              className="bg-[#d4af37] text-black font-semibold px-5 py-2 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition"
            >
              SalveazÄƒ concediul
            </button>
          </div>

          {holidays.length > 0 && (
            <ul className="mt-4 text-center text-sm text-gray-300">
              {holidays.map((h, i) => (
                <li key={i} className="mb-2">
                  {h}{" "}
                  <button
                    onClick={() => handleDeleteHoliday(h)}
                    className="text-red-500 hover:underline ml-2"
                  >
                    È˜terge
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        <div className="flex justify-center mb-6">
          <input
            type="text"
            placeholder="CautÄƒ client dupÄƒ nume, prenume sau telefon..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full max-w-md bg-[#1a1a1a] border border-[#d4af37]/40 text-[#d4af37] placeholder-gray-500 rounded-md px-4 py-2 focus:border-[#d4af37] focus:outline-none"
          />
        </div>

        {message && (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className={`text-center mb-4 font-medium ${
              message.includes("âœ…") ||
              message.includes("ðŸ—‘ï¸") ||
              message.includes("ðŸŒ´") ||
              message.includes("ðŸ§¹")
                ? "text-green-400"
                : "text-red-400"
            }`}
          >
            {message}
          </motion.p>
        )}

        {filteredAppointments.length === 0 ? (
          <p className="text-center text-gray-400">
            Niciun rezultat gÄƒsit pentru termenul introdus.
          </p>
        ) : (
          <div className="overflow-auto rounded-lg">
            <table className="w-full text-sm border-collapse">
              <thead className="bg-[#1a1a1a] text-[#d4af37] sticky top-0">
                <tr>
                  <th className="p-3 border border-[#d4af37]/30">#</th>
                  <th className="p-3 border border-[#d4af37]/30">Client</th>
                  <th className="p-3 border border-[#d4af37]/30">Telefon</th>
                  <th className="p-3 border border-[#d4af37]/30">Serviciu</th>
                  <th className="p-3 border border-[#d4af37]/30">Data</th>
                  <th className="p-3 border border-[#d4af37]/30">Ora</th>
                  <th className="p-3 border border-[#d4af37]/30">AcÈ›iuni</th>
                </tr>
              </thead>
              <tbody>
                {filteredAppointments.map((a, i) => (
                  <tr
                    key={a.id}
                    className="hover:bg-[#d4af37]/10 transition text-center"
                  >
                    <td className="p-2 border border-[#d4af37]/20">{i + 1}</td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.client_prenume} {a.client_nume}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.client_telefon}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {a.service}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="date"
                          min={new Date().toISOString().split("T")[0]}
                          value={edited.date}
                          onChange={(e) =>
                            setEdited((prev) => ({ ...prev, date: e.target.value }))
                          }
                          className="bg-[#111] border border-[#d4af37]/50 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        <span className="text-gray-200">{a.date}</span>
                      )}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20">
                      {editingId === a.id ? (
                        <input
                          type="time"
                          value={edited.time}
                          onChange={(e) =>
                            setEdited((prev) => ({ ...prev, time: e.target.value }))
                          }
                          className="bg-[#111] border border-[#d4af37]/50 rounded-md px-2 py-1 text-[#d4af37]"
                        />
                      ) : (
                        <span className="text-gray-200">{a.time}</span>
                      )}
                    </td>
                    <td className="p-2 border border-[#d4af37]/20 flex justify-center gap-2">
                      {editingId === a.id ? (
                        <button
                          onClick={() => handleSave(a.id)}
                          className="bg-[#d4af37] text-black px-3 py-1 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37]"
                        >
                          SalveazÄƒ
                        </button>
                      ) : (
                        <>
                          <button
                            onClick={() => handleEdit(a)}
                            className="text-[#d4af37] hover:underline"
                          >
                            EditeazÄƒ
                          </button>
                          <button
                            onClick={() => handleDelete(a.id)}
                            className="text-red-500 hover:underline"
                          >
                            È˜terge
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </motion.div>
    </section>
  );
}
