import { useEffect, useState } from "react";

export default function Services() {
  const [services, setServices] = useState([]);

  useEffect(() => {
    fetch("http://localhost/barbershop/frontend/backend/api/get_services.php")
      .then((res) => res.json())
      .then((data) => setServices(data))
      .catch((err) => console.error("Eroare API:", err));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Serviciile disponibile</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {services.map((s) => (
          <div key={s.id} className="border rounded-xl p-4 shadow">
            <h2 className="text-xl font-semibold">{s.name}</h2>
            <p>Durată: {s.duration} min</p>
            <p>Preț: {s.price} lei</p>
          </div>
        ))}
      </div>
    </div>
  );
}
