import { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("bookings")) || [];
    // Sortează programările după dată și oră
    const sorted = stored.sort((a, b) => {
      const da = new Date(a.date.split("/").reverse().join("-") + " " + a.time);
      const db = new Date(b.date.split("/").reverse().join("-") + " " + b.time);
      return da - db;
    });
    setBookings(sorted);
  }, []);

  const handleDelete = (index) => {
    const updated = bookings.filter((_, i) => i !== index);
    setBookings(updated);
    localStorage.setItem("bookings", JSON.stringify(updated));
  };

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white p-10">
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="text-3xl font-bold text-center text-[#d4af37] mb-10"
      >
        Programări — Panou Admin
      </motion.h2>

      {bookings.length === 0 ? (
        <p className="text-center text-gray-400">
          Nu există programări momentan.
        </p>
      ) : (
        <div className="grid md:grid-cols-2 gap-6 max-w-5xl mx-auto">
          {bookings.map((b, i) => (
            <motion.div
              key={i}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.4, delay: i * 0.1 }}
              className="bg-black/50 border border-[#d4af37]/40 rounded-lg p-6 shadow-md hover:border-[#d4af37] transition"
            >
              <h3 className="text-[#d4af37] font-semibold mb-2">
                Programarea #{i + 1}
              </h3>
              <p>
                <span className="text-gray-400">Nume:</span> {b.nume} {b.prenume}
              </p>
              <p>
                <span className="text-gray-400">Telefon:</span> {b.telefon}
              </p>
              <p>
                <span className="text-gray-400">Serviciu:</span> {b.service}
              </p>
              <p>
                <span className="text-gray-400">Data:</span> {b.date || "Nespecificată"}
              </p>
              <p>
                <span className="text-gray-400">Ora:</span> {b.time || "—"}
              </p>

              <button
                onClick={() => handleDelete(i)}
                className="text-red-400 hover:text-red-300 text-sm mt-4"
              >
                Șterge
              </button>
            </motion.div>
          ))}
        </div>
      )}
    </section>
  );
}
