import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Trash2 } from "lucide-react";

export default function Dashboard() {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("bookings") || "[]");
    setBookings(data);
  }, []);

  const handleDelete = (index) => {
    const updated = bookings.filter((_, i) => i !== index);
    setBookings(updated);
    localStorage.setItem("bookings", JSON.stringify(updated));
  };

  return (
    <section className="min-h-screen bg-[#0f0f0f] text-white py-20 px-6">
      <div className="max-w-5xl mx-auto">
        <motion.h1
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold text-center text-[#d4af37] mb-12"
        >
          Panou Administrativ — Programări
        </motion.h1>

        {bookings.length === 0 ? (
          <p className="text-center text-gray-400">
            Nu există încă programări efectuate.
          </p>
        ) : (
          <div className="overflow-x-auto border border-[#d4af37]/20 rounded-xl shadow-lg">
            <table className="min-w-full text-sm text-left">
              <thead className="bg-black/60 text-[#d4af37] uppercase tracking-wider">
                <tr>
                  <th className="px-6 py-3">#</th>
                  <th className="px-6 py-3">Nume Client</th>
                  <th className="px-6 py-3">Data</th>
                  <th className="px-6 py-3">Ora</th>
                  <th className="px-6 py-3 text-center">Acțiune</th>
                </tr>
              </thead>
              <tbody>
                {bookings.map((b, i) => (
                  <motion.tr
                    key={i}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: i * 0.05 }}
                    className="border-t border-[#d4af37]/10 hover:bg-[#1a1a1a] transition"
                  >
                    <td className="px-6 py-3 text-gray-400">{i + 1}</td>
                    <td className="px-6 py-3 font-semibold">{b.name}</td>
                    <td className="px-6 py-3">{b.date}</td>
                    <td className="px-6 py-3">{b.hour}</td>
                    <td className="px-6 py-3 text-center">
                      <button
                        onClick={() => handleDelete(i)}
                        className="text-red-400 hover:text-red-500 transition"
                        title="Șterge rezervarea"
                      >
                        <Trash2 size={18} />
                      </button>
                    </td>
                  </motion.tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </section>
  );
}
