import { motion } from "framer-motion";
import bgImage from "../assets/barbershop.jpg"; // ✅ imaginea de fundal (vei adăuga tu în /src/assets/)

export default function Home() {
  return (
    <section
      className="min-h-screen flex flex-col items-center justify-center text-center relative text-white"
      style={{
        backgroundImage: `url(${bgImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* Overlay negru-transparent pentru contrast */}
      <div className="absolute inset-0 bg-black/70"></div>

      {/* Conținut */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="relative z-10 px-6"
      >
        <h1 className="text-4xl sm:text-5xl md:text-6xl font-extrabold text-[#d4af37] mb-6 drop-shadow-lg">
          Barbershop de clasă pentru Gentlemen adevărați
        </h1>
        <p className="text-gray-300 text-lg max-w-2xl mx-auto mb-8">
          Programează-te și experimentează rafinamentul și precizia unui look perfect.
        </p>

        <motion.a
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          href="/booking"
          className="inline-block bg-[#d4af37] text-black font-semibold py-3 px-6 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition duration-300"
        >
          Programează-te acum
        </motion.a>
      </motion.div>
    </section>
  );
}
