import { useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [telefon, setTelefon] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    const users = JSON.parse(localStorage.getItem("users") || "[]");
    const user = users.find((u) => u.telefon === telefon);

    if (user) {
      localStorage.setItem("loggedUser", JSON.stringify(user));
      // ðŸ”” trimitem semnal global pentru Navbar
      window.dispatchEvent(new Event("userUpdated"));
      alert(`Bun venit, ${user.prenume} ${user.nume}!`);
      navigate("/");
    } else {
      alert("NumÄƒr de telefon inexistent!");
    }
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-[#0f0f0f] text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="bg-black/40 backdrop-blur-md border border-[#d4af37]/30 rounded-2xl p-10 w-full max-w-md shadow-lg"
      >
        <h2 className="text-3xl font-bold text-center text-[#d4af37] mb-6">
          Autentificare Client
        </h2>

        <form onSubmit={handleLogin} className="space-y-6">
          <div>
            <label className="block text-sm text-gray-300 mb-2">
              NumÄƒr de telefon
            </label>
            <input
              type="tel"
              value={telefon}
              onChange={(e) => setTelefon(e.target.value)}
              placeholder="076784211"
              pattern="[0-9+ ]{7,15}"
              title="NumÄƒrul poate conÈ›ine cifre, spaÈ›ii È™i semnul +"
              className="w-full bg-transparent border border-[#d4af37]/30 text-white rounded-md px-4 py-2 focus:outline-none focus:border-[#d4af37] transition"
            />
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full mt-4 bg-[#d4af37] text-black font-semibold py-3 rounded-md hover:bg-transparent hover:text-[#d4af37] border border-[#d4af37] transition duration-300"
          >
            AutentificÄƒ-te
          </motion.button>
        </form>

        <p className="text-center text-gray-400 text-sm mt-6">
          Nu ai cont?{" "}
          <a href="/register" className="text-[#d4af37] hover:underline">
            ÃŽnregistreazÄƒ-te aici
          </a>
        </p>
      </motion.div>
    </section>
  );
}
